\begin{ex}%[1D3H3-3]%[1-thpt-nguyen-du-br-vt-HKI-NH24-25]%[Hector Tran]
	Cho hàm số $f(x)=\dfrac{2x^2-5x+2}{x-2}$.
	\choiceTF
	{\True Tập xác định $\mathscr{D}=\mathbb{R}\setminus\{2\}$}
	{$\lim\limits_{x\to 2}f(x)=\dfrac{3}{2}$}
	{\True $f(2)$ không tồn tại}
	{\True Hàm số đã cho gián đoạn tại điểm $x_0=2$}
	\loigiai{
		% Lời giải chung
		\begin{itemchoice}
			\itemch Điều kiện xác định $x-2\neq 0\Leftrightarrow x\neq 2$.\\ 
			Vậy $\mathscr{D}=\mathbb{R}\setminus\{2\}$.
			\itemch Ta có $\lim\limits_{x\to 2}f(x)=\lim\limits_{x\to 2}\dfrac{(2x-1)(x-2)}{x-2}=\lim\limits_{x\to 2}f(x)=\lim\limits_{x\to 2}(2x-1)=3$.
			\itemch Hàm số $f(x)$ không xác định tại $x=2$ nên $f(2)$ không tồn tại.
			\itemch Ta có hàm số không xác định tại $x_0=2$ nên hàm số gián đoạn tại $x_0=2$.
		\end{itemchoice}
	}
\end{ex}

