	\choiceTF
	{\True Hàm số có $2$ điểm cực trị}
	{Giá trị cực đại của hàm số là $\dfrac{4}{3}$}
	{$f\left(10^{79}\right)<f\left(10^{80}\right)$}
	{\True Khoảng cách giữa $2$ điểm cực trị của đồ thị hàm số là $\dfrac{4 \sqrt{145}}{27}$}
	\loigiai{
		Ta có $y'=-3x^2+4x$. Cho $y'=0\Leftrightarrow \hoac{&x=-\dfrac{4}{3}\\&x=0.}$\\
		Bảng biến thiên
		\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
				\tkzTabInit[nocadre=false,lgt=1,espcl=2,deltacl=0.5]
				{$x$/.7 ,$y'$/.7,$y$/2}
				{$-\infty$ , $-\dfrac{4}{3}$ , $0$ , $+\infty$}
				\tkzTabLine{ , - , $0$ , + , $0$ , - , }
				\tkzTabVar{+/$+\infty$ , -/$-\dfrac{49}{27}$ , +/$-3$ , -/$-\infty$}
			\end{tikzpicture}
		\end{center}
		Dựa vào bảng biến thiên nhận xét
		\begin{itemchoice}
			\itemch \textbf{Đúng} Hàm số có $2$ điểm cực trị.
			\itemch \textbf{Sai} Giá trị cực đại của hàm số là $-3$.
			\itemch \textbf{Sai} Hàm số nghịch biến trên $\left(0;+\infty\right)$ và $10^{79}<10^{80}$ nên $f\left(10^{79}\right)>f\left(10^{80}\right)$.
			\itemch \textbf{Đúng} Gọi $A\left(-\dfrac{4}{3};-\dfrac{49}{23}\right)$, $B(0;-3)$ là hai điểm cực trị.\\
			Ta có $\overrightarrow{AB}=\left(\dfrac{4}{3};\dfrac{32}{27}\right)$ suy ra $AB=\sqrt{\left(\dfrac{4}{3}\right)^2+\left(\dfrac{32}{27}\right)^2}=\dfrac{4\sqrt{145}}{27}$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1V5-8]%[Lớp 12 - Đề học kì 1 - Đề 1]%[Nguyễn Văn Sang]
	Một chất điểm chuyển động theo phương trình $S=-t^3+9 t^2+21 t+9$ trong đó $t$ tính bằng giây $(s)$ và $S$ tính bằng mét $(m)$. Xét tính đúng sai của các khẳng định sau.
	\choiceTF
	{$v(t)=-3 t^2+18 t+2$}
	{\True Vận tốc của chất điểm tại giây thứ $2$ là $45$ m/s}
	{Vận tốc của chất điểm tại thời điểm gia tốc triệt tiêu là $45$ m/s}
	{\True Vận tốc chuyển động đạt giá trị lớn nhất tại thời điểm $t=3$ s}
	\loigiai{
		Ta có
		\begin{itemchoice}
			\itemch \textbf{Sai} Vận tốc $v(t) = S'(t) = -3t^2 + 18t + 21$.
			\itemch \textbf{Đúng} Tại $t=2$, $v(2) = -3(2)^2 + 18(2) + 21 = -12 + 36 + 21 = 45$ m/s.
			\itemch \textbf{Sai} Gia tốc $a(t) = v'(t) = -6t + 18$.\\ 
			Gia tốc triệt tiêu khi $a(t) = 0 \Leftrightarrow -6t + 18 = 0 \Leftrightarrow t = 3$.\\ 
			Khi đó $v(3) = -3(3)^2 + 18(3) + 21 = -27 + 54 + 21 = 48$ m/s.
			\itemch \textbf{Đúng} Để tìm thời điểm vận tốc lớn nhất, ta xét $v'(t) = a(t) = -6t + 18 = 0 \Leftrightarrow t = 3$.\\ 
			Vì $v''(t) = a'(t) = -6 < 0$ nên $v(t)$ đạt cực đại tại $t=3$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1H4V4-4]%[Dự án đề kiểm tra Toán 12 HKI NH24-25-Trần Xuân Hòa]%[Kẻ sắt - Hải dương]
	Cho hình chóp $S.A B C D$ có đáy là hình bình hành $A B C D$, $O$ là giao điểm của $A C$ và $B D$. Tam giác $S C D$ là tam giác đều cạnh $2$. Mặt phẳng $(P)$ đi qua $O$ và song song với mặt phẳng $(S C D)$. Tính diện tích hình tạo bởi mặt phẳng $(P)$ và các mặt của hình chóp $S.A B C D$ (làm tròn đến hàng phần mười).
	\shortans[oly]{$1{,}3$}
	\loigiai
	{
		\begin{center}
			\begin{tikzpicture}[scale=0.7, font=\footnotesize,line join=round, line cap=round, >=stealth]
				\path 
				(0,0) coordinate (A)
				++(-140:3.5) coordinate (B)
				++(0:5.5) coordinate (C)
				($(A)+(C)-(B)$) coordinate (D)
				($(A)+(0,3)$) coordinate (S)
				($(A)!1/2!(C)$) coordinate (O)
				($(B)!1/2!(C)$) coordinate (E)
				($(S)!1/2!(B)$) coordinate (F)
				($(S)!1/2!(A)$) coordinate (G)
				($(A)!1/2!(D)$) coordinate (H)
				($(C)!1/2!(D)$) coordinate (K)
				;
				\coordinate (I) at (intersection of A--K and O--H);
				\foreach \i in{B,C,D}{\draw (S)--(\i);};
				\draw (B)--(C)--(D) (E)--(F) (S)--(K);
				\draw[dashed] (S)--(A)--(B) (A)--(D) (F)--(G)--(H)--(E) (A)--(K) (I)--(G);
				\pic[draw,angle eccentricity=1.8,angle radius=2mm]{right angle=S--A--D};
				\foreach \i/\g in {A/-90,B/-90,C/-90,D/-90,S/90,O/-90,E/-90,F/180,G/40,H/-80,K/-90,I/-40}
				\fill[black] (\i) circle(1pt)+(\g:3mm)node[scale=1]{$\i$};
			\end{tikzpicture}
		\end{center}
		Gọi $E$, $F$, $G$, $H$, $K$ lần lượt là trung điểm của $BC$, $SB$, $SA$, $AD$, $CD$ và $I$ là giao điểm của $AK$ và $EH$.\\
		Dễ dàng xác định được mặt phẳng $(P)$ chính là mặt phẳng $(EFGH)$. Hình tạo bởi mặt phẳng $(P)$ và các mặt hình chóp là hình thang $EFGH$. Ta có
		\begin{itemize}
			\item $EF$ là đường trung bình của $\triangle SBC$.
			\item $FG$ là đường trung bình của $\triangle SAB$.
			\item $GH$ là đường trung bình của $\triangle SAD$.
			\item $GK$ là đường trung bình của $\triangle SAK$.
		\end{itemize}
		Do đó $EF=FG=GH=\dfrac{SC}{2}=1$ và $EH=CD=2$.\\
		Vì $\triangle SCD$ đều cạnh $2$ nên $SK=\sqrt{3}$.\\
		Ta có $SK\parallel GI$ và $EH\parallel CD$ mà $SK\perp CD$ nên $GI\perp EH$.\\
		Tứ giác $EFGH$ là hình thang có đường cao $GI=\dfrac{SK}{2}=\dfrac{\sqrt{3}}{2}$.\\
		Diện tích hình thang $EFGH$ là $$\dfrac{(FG+EH)GI}{2}=\dfrac{(1+2)\cdot \sqrt{3}}{4}\approx 1{,}3.$$
		Vậy diện tích cần tìm là $1{,}3$.
	}
\end{ex}

