\begin{ex}%[2D1V5-1]%[Phan Ngọc Hiền - Lớp 12 - Đề cuối học kì 1 - Cà Mau]%[Phạm Văn Long]
	\immini{Cho hàm số $f(x)=ax^3+bx^2+cx+d$ có đồ thị như hình vẽ.
		\choiceTF
		{Hàm số đã cho nghịch biến trên $(-\infty;1)$}
		{\True Hàm số đạt cực tiểu tại $x=0$}
		{\True Đồ thị hàm số cắt trục hoành tại $1$ điểm}
		{\True Hàm số đã cho là $y=-2x^3+3x+1$}}
	{\begin{tikzpicture}[scale=0.9, font=\footnotesize,line join=round, line cap=round,thick, >=stealth];
			\draw[->] (-2,0) -- (3,0) node[below] {\scriptsize $x$};
			\draw[->] (0,-1.6) -- (0,4) node[left] {\scriptsize $y$};
			\draw (0.1,0.1)node[below left]{\scriptsize $O$};
			\draw[thin,samples=150,smooth,domain=-0.75:1.8] plot(\x,{-2.04*((\x)^3)+3.08*((\x)^2)-0.05*(\x)+1.0});		
			\draw[dashed] (1,0)node[below]{$1$} -- (1,2)--(0,2);
			\draw (-0.2,0.8)node {$1$} (-0.2,2)node {$2$};
			\draw[fill=black]  (1,2) circle (1.2pt) (0,1) circle (1.2pt);	
	\end{tikzpicture}}
	\loigiai{	
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Hàm số đã cho nghịch biến trên $(-\infty;0)$.
			\itemch \textbf{Đúng}.\\
			Hàm số đạt cực tiểu tại $x=0$.
			\itemch \textbf{Đúng}.\\
			Đồ thị hàm số cắt trục hoành tại $1$ điểm.
			\itemch \textbf{Sai}.
			Ta có $f'(x)=3ax^2+2bx+c$.\\
			Từ hình vẽ ta có hàm số $\heva{&f'(0)=0\\&f'(1)=0\\&f(1)=2\\&f(0)=1}\Rightarrow \heva{&c=0\\&3a+2b+c=0\\&a+b+c+d=2\\&d=1}\Rightarrow \heva{&a=-2\\&b=3\\&c=0\\&d=1.}$\\
			Vậy $y=-2x^3+3x+1$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2H2-4]%[Phan Ngọc Hiền - Lớp 12 - Đề cuối học kì 1 - Cà Mau]%[Phạm Văn Long]
	Trong không gian toạ độ $Oxyz$, cho hai véc-tơ $\overrightarrow{u}=(-1; 2; 2)$ và $\overrightarrow{v}=(-3;-4; 0)$.
	\choiceTF
	{\True Nếu một véc-tơ có tọa độ là $(x; y; z)$ thì véc-tơ đó có độ dài là $\sqrt{x^2+y^2+z^2}$}
	{$\overrightarrow{u}$, $\overrightarrow{v}$ cùng phương}
	{$\overrightarrow{u} \cdot \overrightarrow{v}=5$}
	{Góc giữa hai véc-tơ $\overrightarrow{u}$ và $\overrightarrow{v}$ (làm tròn đến hàng đơn vị của độ) là $115^{\circ}$}
	\loigiai{	
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Nếu một véc-tơ có tọa độ là $(x; y; z)$ thì véc-tơ đó có độ dài là $\sqrt{x^2+y^2+z^2}$.
			\itemch \textbf{Sai}.\\
			Ta có $\dfrac{-1}{-3}\ne \dfrac{2}{-4}$ nên $\overrightarrow{u}$, $\overrightarrow{v}$ không cùng phương.
			\itemch \textbf{Sai}.\\
			Ta có $\overrightarrow{u} \cdot \overrightarrow{v}=(-1)\cdot (-3)+2\cdot (-4)+2\cdot 0=-5$.
			\itemch \textbf{Sai}.\\
			Ta có $\cos \left(\overrightarrow{u},\overrightarrow{v}\right)=\dfrac{\overrightarrow{u} \cdot \overrightarrow{v}}{\left |\overrightarrow{u}\right | \cdot \left |\overrightarrow{v}\right |}=\dfrac{-5}{3\cdot 5}=-\dfrac{1}{3}$.\\
			Vậy $\left(\overrightarrow{u},\overrightarrow{v}\right)\approx 108^\circ$.
		\end{itemchoice}
	}
\end{ex}

