	\begin{center}
		\begin{tabular}{|c|c|c|c|c|}
			\hline
			Mức xà & {$[170; 172)$} & {$[172; 174)$} & {$[174; 176)$} & {$[176; 178)$} \\
			\hline
			Số vận động viên & 3 & 10 & 6 & 1 \\
			\hline
		\end{tabular}
	\end{center}
	\choiceTF
	{\True Khoảng biến thiên của mẫu số liệu ghép nhóm này là $8$}
	{\True Thành tích trung bình của môn nhảy cao là $\bar{x}=173{,}5$}
	{Phương sai của mẫu số liệu ghép nhóm (làm tròn đến hàng phần chục) là $2{,}3$}
	{\True Độ lệch chuẩn của mẫu số liệu ghép nhóm (làm tròn đến hàng phần chục) là $1{,}5$}
	\loigiai{	
		\begin{center}
			$\begin{array}{|c|c|c|}
				\hline \text { Nhóm } & \text { Tần số } & \begin{array}{c}
					\text { Tần số } \\
					\text { tích luỹ }
				\end{array} \\
				\hline
				{[170 ; 172)} & 3 & 3 \\
				{[172 ; 174)} & 10 & 13 \\
				{[174 ; 176)} & 6 & 19 \\
				{[176 ; 178)} & 1 & 20 \\				
				\hline & n=20 & \\
				\hline
			\end{array}$
		\end{center}
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Khoảng biến thiên của mẫu số liệu ghép nhóm này là $178-170=8$.
			\itemch \textbf{Đúng}.\\
			Thành tích trung bình của môn nhảy cao là $$\bar{x}=\dfrac{171\cdot 3+173\cdot 10+175\cdot 6+177}{20}=173{,}5.$$
			\itemch \textbf{Sai}.\\
			Phương sai của mẫu số liệu ghép nhóm là
			$$s^2=\dfrac{(171-173{,}5)^2\cdot 3+(173-173{,}5)^2\cdot 10+(175-173{,}5)^2\cdot 6+(177-173{,}5)^2}{20}\approx 2{,}4.$$
			\itemch \textbf{Sai}.\\
			Độ lệch chuẩn của mẫu số liệu ghép nhóm là $\sqrt{2{,}4}\approx 1{,}5$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1H2-1]%[KNTT - Lớp 12 - Kiểm tra học kì 1 - 2024-2025]%[Trần Văn Hùng]
