\begin{ex}%[2H2V2-2]%[KNTT - Lớp 12 - TRƯỜNG PT DTNT Tỉnh Phú Yên- ĐỀ THI HK1]%[Đắc Vũ]
	Trong không gian với hệ trục tọa độ $Oxyz$, cho ba điểm $A(1; 2; 3)$, $B(2; 1; 5)$, $C(2; 4; 2)$.  
	\choiceTF
	{Điểm $I(a; b; c)$ nằm trên mặt phẳng $(Oxz)$ thỏa mãn $|3\overrightarrow{IB} - \overrightarrow{IC}|$ đạt giá trị nhỏ nhất. Khi đó $a - 2b + 2c = 15$}
	{Góc giữa hai vectơ $\overrightarrow{AB}$ và $\overrightarrow{AC}$ bằng $30^\circ$}
	{\True $\overrightarrow{OA} + \overrightarrow{OB} + \overrightarrow{OC} = (5; 7; 10)$}
	{\True Tọa độ trung điểm của $AB$ là $\left(\dfrac{3}{2};\dfrac{3}{2};4\right)$}
	\loigiai{
		\begin{itemchoice}
			\itemch 
			Lấy điểm $M$ sao cho $3\overrightarrow{MB}-\overrightarrow{MC}$. Khi đó $M\left(2;-\dfrac{1}{2};4\right)$.\\
			Ta có $\left|3\overrightarrow{IB}-\overrightarrow{IC}\right|=\left|3\overrightarrow{IM}+3\overrightarrow{MB}-\overrightarrow{IM}-\overrightarrow{MC}\right|=\left|2\overrightarrow{IM}\right|=2IM$.\\
			$\left|3\overrightarrow{IB}-\overrightarrow{IC}\right|$ đạt giá trị nhỏ nhất khi $IM$ nhỏ nhất.\\
			Suy ra $I$ là hình chiếu $M$ lên mặt phẳng $\left(Oxz\right)$ nên $I\left(2;0;4\right)$.\\
			Do đó ta có $a-2b+2c=10$. 
			\itemch Ta có $\overrightarrow{AB}=(1;-1;2)$ và $\overrightarrow{AC}=(1;2;-1)$.\\
			Khi đó
			\[\cos \left(\overrightarrow{AB},\overrightarrow{AC}\right)=\dfrac{1 \cdot 1+ (-1) \cdot 2 +2 \cdot (-1)}{\sqrt{1^2+(-1)^2+2^2} \cdot \sqrt{1^2+2^2+(-1)^2}}=-\dfrac{1}{2}.\]
			Nên góc giữa hai vectơ $\overrightarrow{AB}$ và $\overrightarrow{AC}$ là $120^\circ$. 
			\itemch Ta có $\overrightarrow{OA} + \overrightarrow{OB} + \overrightarrow{OC} = (5; 7; 10)$.
			\itemch Tọa độ trung điểm của $AB$ là $\left(\dfrac{3}{2};  \dfrac{3}{2}; 4\right)$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H2H2-2]%[KNTT - Lớp 12 - TRƯỜNG PT DTNT Tỉnh Phú Yên- ĐỀ THI HK1]%[Nguyễn Trần Anh Tuấn]
	Trong không gian $Oxyz$ cho ba điểm $A(3;2;-1)$, $B(-1;x;1)$, $C(7;-1;y)$. Khi $A$, $B$, $C$ thẳng hàng thì giá trị biểu thức $x+y$ bằng bao nhiêu?
	\shortans[oly]{-8}
	\loigiai{
		Ta có $\overrightarrow{AB} = (-4; -x - 2; 2)$; $\overrightarrow{AC} = (4; -3; y + 1)$.\\
		Để $A$, $B$, $C$ thẳng hàng thì \[\dfrac{4}{-4}=\dfrac{-3}{-x-2}=\dfrac{2}{y+1} \Leftrightarrow \heva{&x+2=-3\\&y+1=-2} \Leftrightarrow \heva{&x=-5\\&y=-3.}\]
		Vậy $x+y=-5-3=-8$.}
\end{ex}

\begin{ex}%[2D1N4-1]%[KNTT - Lớp 12 - TRƯỜNG PT DTNT Tỉnh Phú Yên- ĐỀ THI HK1]%[Nguyễn Trần Anh Tuấn]
	Đồ thị hàm số $y = \dfrac{x^2-2x+5}{x^2-1}$ có bao nhiêu đường tiệm cận?
	\shortans[oly]{3}
	\loigiai{
		Ta có 
		\begin{itemize}
			\item $\lim\limits_{x\to \pm \infty}y=\lim\limits_{x\to \pm \infty} \left(\dfrac{x^2-2x+5}{x^2-1}\right)=\lim\limits_{x\to \pm \infty} \left(\dfrac{1-\dfrac{2}{x}+\dfrac{5}{x^2}}{1-\dfrac{1}{x^2}}\right)=1$ do đó $y=1$ là tiệm cận ngang của đồ thị hàm số.
			\item $\lim\limits_{x\to 1^{+}}y=\lim\limits_{x\to 1^{+}}\left(\dfrac{x^2-2x+5}{x^2-1}\right)=+\infty$ do đó $x=1$ là tiệm cận đứng của đồ thị hàm số.
			\item $\lim\limits_{x\to -1^{+}}y=\lim\limits_{x\to -1^{+}}\left(\dfrac{x^2-2x+5}{x^2-1}\right)=-\infty$ do đó $x=-1$ là tiệm cận đứng của đồ thị hàm số.
		\end{itemize}
		Vậy đồ thị hàm số có ba đường tiệm cận.
	}
\end{ex}

