		\begin{tikzpicture}[scale=0.5, font=\footnotesize, line join=round, line cap=round, >=stealth]
			% \draw[gray!50,xstep = 1, ystep = 1,dashed] (0,0) grid (12,5);
			\fill[gray!30]
			(0,0) rectangle +(1,1)
			(0,5) rectangle +(1,1)
			(5,5) rectangle +(1,1)
			(5,0) rectangle +(1,1)
			;
			\draw
			(0,0) rectangle +(6,6)
			(1,1) rectangle +(4,4)
			(0,1)-|(1,0)
			(0,5)-|(1,6)
			(5,0)|-(6,1)
			(6,5)-|(5,6)
			;
