			(7,1) rectangle +(4,1)
			(7,2)--++(1,2)--++(4,0)--++(-1,-2)
			(11,1)--++(1,2)
			(8,3)--++(0,1)
			(12,3)--++(0,1)
			(7.5,2)--++(.5,1)--++(3.5,0)
			;
			\draw[dashed]
			(7,1) --++(.5,1)
			(12,3)--++(-.5,0)
			;
		\end{tikzpicture}
	\end{center}
	\shortans[oly]{3}
	\loigiai{
		Hình hộp có đáy là hình chữ nhật kích thước $24-2x$ và $15-2x$.\\
		Chiều cao của hình hộp là $x$.\\
