\begin{ex}%[2H2V2-2]%[Lớp 12 - Đề thi HK1 - THPT Nguyễn Thái Bình - TPHCM]%[Tư Đô Nguyên]
	Trong không gian với hệ tọa độ $Oxyz$, cho ba điểm $A(1; 3; 2)$, $B(2;-1; 4)$, $C(5; 1;-2)$. Khi đó
	\choiceTF
	{Để $C$ là trọng tâm tam giác $ABM$ thì tọa độ $M(12; 1; 12)$}
	{Khoảng cách giữa hai điểm $B$ và $C$ là $BC=2\sqrt{2}$}
	{\True Giá trị côsin của góc giữa hai vectơ $\overrightarrow{AB}$ và $\overrightarrow{AC}$ là $\cos \left(\overrightarrow{AB}, \overrightarrow{AC}\right)=\dfrac{2\sqrt{21}}{63}$}
	{\True Điểm $E$ thỏa mãn $\overrightarrow{EA}-3\overrightarrow{EC}=\overrightarrow0$ thì $E(7; 0;-4)$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Gọi $M(x_M;y_M;z_M)$ là điểm thỏa mãn $C$ là trọng tâm tam giác $ABM$. Khi đó
			\allowdisplaybreaks
			\begin{eqnarray*}
				\heva{
					&3x_C=x_A+x_B+x_M \\
					&3y_C=y_A+y_B+y_M \\
					&3z_C=z_A+z_B+z_M
				}
				\Leftrightarrow
				\heva{
					&3\cdot 5=1+2+x_M \\
					&3\cdot 1=3+(-1)+y_M \\
					&3\cdot (-2)=2+4+z_M
				}
				\Leftrightarrow
				\heva{
					&x_M=12 \\
					&y_M=1 \\
					&z_M=-12.
				}
			\end{eqnarray*}
			Vậy $M(12;1;-12)$ là điểm thỏa yêu cầu.
			\itemch \textbf{Sai}.\\
			Ta có $BC=\sqrt{(5-2)^2+(1+1)^2+(-2-4)^2}=7$.
			\itemch \textbf{Đúng}.\\
			Ta có $\overrightarrow{AB}=(1;-4;2)$ và $\overrightarrow{AC}=(4;-2;-4)$. Khi đó
			\allowdisplaybreaks
			\begin{eqnarray*}
				\cos\left(\overrightarrow{AB},\overrightarrow{AC}\right)=\dfrac{\overrightarrow{AB}\cdot \overrightarrow{AC}}{AB\cdot AC}=\dfrac{1\cdot 4+(-4)\cdot (-2)+2\cdot (-4)}{\sqrt{1^2+(-4)^2+2^2}\cdot \sqrt{4^2+(-2)^2+(-4)^2}}=\dfrac{2\sqrt{21}}{63}.
			\end{eqnarray*}
			\itemch \textbf{Đúng}.\\
			Gọi $E(x_E;y_E;z_E)$ là điểm thỏa mãn $\overrightarrow{EA}-3\overrightarrow{EC}=\overrightarrow0$. \\
			Khi đó $\heva{
				&\overrightarrow{EA}=(1-x_E;3-y_E;2-z_E)\\
				&\overrightarrow{EC}=(5-x_E;1-y_E;-2-z_E).
			}$\\
			Ta có
			\allowdisplaybreaks
			\begin{eqnarray*}
				\overrightarrow{EA}-3\overrightarrow{EC}=\overrightarrow 0 \Leftrightarrow
				\heva{
					&(1-x_E)-3(5-x_E)=0\\
					&(3-y_E)-3(1-y_E)=0\\
					&(2-z_E)-3(-2-z_E)=0\\
				}\Leftrightarrow	\heva{
					& x_E=7\\
					&y_E=0\\
					&z_E=-4.\\
				}
			\end{eqnarray*}
			Vậy $E(7;0;-4)$ là điểm thỏa yêu cầu.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1H5-8]%[Lớp 12 - Đề thi HK1 - THPT Nguyễn Thái Bình - TPHCM]%[Tư Đô Nguyên]
