\begin{ex}%[2D3H2-3]%[Lớp 12 - Đề thi HK1 - THPT Nguyễn Thái Bình - TPHCM]%[Tư Đô Nguyên]
	Kết quả đo cân nặng của $100$ học sinh lớp $12$ ở trường THPT A được cho bởi bảng sau
	\begin{center}
		\begin{tabular}{|l|c|c|c|c|c|}
			\hline
			Cân nặng (kg) & {$[35; 40)$} & {$[40; 45)$} & {$[45; 50)$} & {$[50; 55)$} & {$[55; 60)$} \\
			\hline
			Số học sinh & 20 & 40 & 25 & 10 & 5 \\
			\hline
		\end{tabular}
	\end{center}
	\choiceTF
	{\True Khoảng biến thiên của mẫu số liệu ghép nhóm là $25$}
	{Khoảng tứ phân vị của mẫu số liệu ghép nhóm là $7$}
	{\True Phương sai của mẫu số liệu ghép nhóm bằng $28{,}5$}
	{Độ lệch chuẩn của mẫu số liệu ghép nhóm bằng $4$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}.\\
			Khoảng biến thiên của mẫu số liệu ghép nhóm là $60-35=25$.
			\itemch \textbf{Sai}.\\
			\begin{itemize}
				\item Tứ phân vị thứ nhất của mẫu số liệu gốc là $\dfrac{1}{2}\left(x_{25}+x_{26}\right) \in [40;45)$. \\
				Nhóm chứa tứ phân vị thứ nhất của mẫu số liệu là $[40;45)$. \\
				Tứ phân vị thứ ba của mẫu số liệu ghép nhóm là
				\allowdisplaybreaks
				\begin{eqnarray*}
					Q_{1}=40+\dfrac{\dfrac{1\cdot 100}{4}-20}{40} \cdot (45-40)=40{,}625.
				\end{eqnarray*}
				\item Tứ phân vị thứ ba của mẫu số liệu gốc là $\dfrac{1}{2}\left(x_{75}+x_{76}\right)\in [45;50)$. \\
				Nhóm chứa tứ phân vị thứ ba của mẫu số liệu là $[45;50)$. \\
				Tứ phân vị thứ ba của mẫu số liệu ghép nhóm là
				\allowdisplaybreaks
				\begin{eqnarray*}
					Q_{3}=45+\dfrac{\dfrac{3\cdot 100}{4}-(20+40)}{25} \cdot (50-45)=48.
				\end{eqnarray*}
			\end{itemize}
			Do đó, khoảng tứ phân vị của mẫu số liệu ghép nhóm trên là $\Delta_{Q}=Q_3-Q_1=48-40{,}625=7{,}375$.
			\itemch \textbf{Đúng}.\\
			Ta có
			\begin{center}
				\begin{tabular}{|l|c|c|c|c|c|}
					\hline
					Cân nặng (kg) & {$[35; 40)$} & {$[40; 45)$} & {$[45; 50)$} & {$[50; 55)$} & {$[55; 60)$} \\
					\hline
					Cân nặng đại diện (kg) & 37,5 & 42,5 & 47,5 & 52,5 & 57,5
					\\
					\hline
					Số học sinh & 20 & 40 & 25 & 10 & 5 \\
					\hline
				\end{tabular}
			\end{center}
			Số trung bình của mẫu số liệu ghép nhóm là
			\[
			\overline{x}=\dfrac{1}{100}\left(20\cdot 37{,}5 +40\cdot 42{,}5+25\cdot 47{,}5+10\cdot 52{,}5+5\cdot 57{,}5\right) = 44{,}5.
			\]
			Phương sai của mẫu số liệu ghép nhóm
			\begin{eqnarray*}
				S^2&=& \dfrac{1}{10}\left(20\cdot 37{,}5^2 +40\cdot 42{,}5^2+25\cdot 47{,}5^2+10\cdot 52{,}5^2+5\cdot 57{,}5^2\right)-44{,}5^2=28{,}5.
			\end{eqnarray*}
			\itemch \textbf{Sai}.\\
			Độ lệch chuẩn của mẫu số liệu ghép nhóm là $S=\sqrt{S^2}\approx5{,}34$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D1V5-3]%[Lớp 12 - Đề thi HK1 - THPT Nguyễn Thái Bình - TPHCM]%[Tư Đô Nguyên]
