\begin{ex}%[2H2V1-4]%[Lớp 12 - Đề thi HK1 - THPT Nguyễn Thái Bình - TPHCM]%[Tư Đô Nguyên]
	Có ba lực cùng tác động vào một chất điểm. Hai trong ba lực này tạo với nhau một góc $80^\circ$ và có độ lớn đều bằng $60$ N, lực còn lại cùng tạo với hai lực kia một góc $60^\circ$ và có độ lớn bằng $50$ N. Tính độ lớn của hợp lực của ba lực trên (làm tròn đến hàng đơn vị).
	\par\shortans[oly]{130}
	\loigiai{
		Gọi $\overrightarrow{F_1}$, $\overrightarrow{F_2}$, $\overrightarrow{F_3}$ là ba lực cần tìm. \\
		Ta có $\left(\overrightarrow{F_1}, \overrightarrow{F_2}\right)=80^\circ, \left|\overrightarrow{F_1}\right|=\left|\overrightarrow{F_2}\right|=60\,\mathrm{N}$, $\left(\overrightarrow{F_2}, \overrightarrow{F_3}\right)=60^\circ$, $\left|\overrightarrow{F_3}\right|=50\,\mathrm{N}$ , $\left(\overrightarrow{F_1}, \overrightarrow{F_3}\right)=60^\circ$.\\
		Hơn nữa, tổng lực tác dụng lên vật $\overrightarrow{F}=\overrightarrow{F_1}+\overrightarrow{F_2}+\overrightarrow{F_3}$.
		\[
		\heva{
		&\overrightarrow{F_1}\cdot \overrightarrow{F_2}=\left|\overrightarrow{F_1}\right|\cdot \left|\overrightarrow{F_2}\right|\cdot \cos \left(\overrightarrow{F_1}, \overrightarrow{F_2}\right)=3\,600 \cos 80^{\circ}
		\\
		&\overrightarrow{F_1}\cdot \overrightarrow{F_3}=\left|\overrightarrow{F_1}\right|\cdot \left|\overrightarrow{F_3}\right|\cdot \cos \left(\overrightarrow{F_1}, \overrightarrow{F_3}\right)=3\,000 \cos 60^{\circ}
		\\
		&\overrightarrow{F_1}\cdot \overrightarrow{F_3}=\left|\overrightarrow{F_1}\right|\cdot \left|\overrightarrow{F_3}\right|\cdot \cos \left(\overrightarrow{F_1}, \overrightarrow{F_3}\right)=3\,000 \cos 60^{\circ}
		}
		\]
		Ta có
		\begin{eqnarray*}
			& & \overrightarrow{F}=\overrightarrow{F_1}+\overrightarrow{F_2}+\overrightarrow{F_3}\\
			&\Rightarrow &\overrightarrow{F}^2=\overrightarrow{F_1}^2+\overrightarrow{F_2}^2+\overrightarrow{F_3}^2 +2\cdot\overrightarrow{F_1}\cdot \overrightarrow{F_2} +2\cdot\overrightarrow{F_1}\cdot \overrightarrow{F_3}+2\cdot\overrightarrow{F_2}\cdot \overrightarrow{F_3}. \\
			&\Rightarrow &\left|\overrightarrow{F}\right|^2=\left|\overrightarrow{F_1}\right|^2+\left|\overrightarrow{F_2}\right|^2+\left|\overrightarrow{F_3}\right|^2+2\cdot\overrightarrow{F_1}\cdot \overrightarrow{F_2} +2\cdot \overrightarrow{F_1}\cdot \overrightarrow{F_3}+ 2\cdot \overrightarrow{F_2}\cdot \overrightarrow{F_3}.\\
			&\Rightarrow & \left|\overrightarrow{F}\right| =\sqrt{\left|\overrightarrow{F_1}\right|^2+\left|\overrightarrow{F_2}\right|^2+ \left|\overrightarrow{F_3}\right|^2+2\cdot\overrightarrow{F_1}\cdot \overrightarrow{F_2} +2\cdot \overrightarrow{F_1}\cdot \overrightarrow{F_3}+2\cdot \overrightarrow{F_2}\cdot \overrightarrow{F_3}}.\\
			&\Rightarrow& \left|\overrightarrow{F}\right|=130 \,\mathrm{N}.\\
		\end{eqnarray*}
	}
\end{ex}

