\begin{ex}%[1D3H3-3]%[Dự án đề kiểm tra Toán khối 11 HKI NH24-25- Dot 7 - Khắc Thiên]%[THPT Văn Bàn - Điện Biên]
	\begin{enumerate}
	\item Tìm giới hạn $\displaystyle\lim_{x\rightarrow 2}\dfrac{x^2-3x+2}{x-2}$.
	\item Cho hàm số $f(x)=\heva{&x^2+3x-1&\text{ khi } x>2\\&mx-1&\text{ khi } x\leq 2}$. Tìm giá trị của $m$ để hàm số liên tục tại điểm $x=2$.
	\end{enumerate}
\loigiai{
	\begin{enumerate}
	\item $\displaystyle\lim_{x\rightarrow 2}\dfrac{x^2-3x+2}{x-2}=\lim_{x\rightarrow 2}\dfrac{(x-1)(x-2)}{x-2}=\lim_{x\rightarrow 2}(x-1)=2-1=1$.
	\item 
	Ta có \begin{itemize}
	\item 	$f(2)=2m-1$.
	\item 	$\displaystyle\lim_{x\rightarrow 2^{+}}f(x)=\lim_{x\rightarrow 2^{+}}x^2+3x-1=2^2+3\cdot2-1=9$.
	\item $\displaystyle\lim_{x\rightarrow 2^{-}}f(x)=\lim_{x\rightarrow 2^{-}}mx-1=2m-1$.
	\end{itemize}
	Để hàm số $f(x)$ liên tục tại $x=2$ khi $f(2)=\displaystyle\lim_{x\rightarrow 2^{+}}f(x)=\lim_{x\rightarrow 2^{-}}f(x)$ hay $2m-1=9$.\\
	Từ đó suy ra $m=5$.\\
	Vậy với $m=5$ thì hàm số đã cho liên tục tại $x=2$.
	\end{enumerate}
}
\end{ex}

\begin{ex}%[1H4V1-4]%[Dự án đề kiểm tra Toán khối 11 HKI NH24-25- Dot 7 - Khắc Thiên]%[THPT Văn Bàn - Điện Biên]
