	{\True $\overrightarrow{BC}\cdot\overrightarrow{AC'} = 25$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[line cap=round,line join=round,=>stealth,font=\footnotesize]
				\path
				(0,0) coordinate (A)
				(-1,-1) coordinate (B)
				(3,0) coordinate (D)
				(2,-1) coordinate (C)
				;
				\foreach \x in {A,B,C,D}{\coordinate (\x') at ($(\x)+(0,3)$);}
				\path 
				($(C)!0.5!(C')$) coordinate (M)
				;
				\draw (A')--(B')--(B)--(C)--(D)--(D')--(A') (B')--(C')--(D') (C)--(C');
				\draw[dashed] (D)--(A)--(B) (A')--(A);
				\foreach \i/\g in {A/160, C/0, B/180, D/0, A'/180, C'/0, B'/180, D'/0,M/0}
				{\draw ($(\i)+(\g:3mm)$) node{$\i$};\fill[black] (\i) circle(1pt);}
			\end{tikzpicture}
		\end{center}
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Vì $M$ là trung điểm $CC'$ nên $\overrightarrow{AM}=\dfrac{1}{2}\left(\overrightarrow{AC}+\overrightarrow{AC'}\right)$. Do đó $AM = \dfrac{1}{2} \left| \overrightarrow{AC} + \overrightarrow{AC'} \right|$.
			\itemch \textbf{Sai}. Ta có $\left(\overrightarrow{DB},\overrightarrow{B'C'}\right)=\left(\overrightarrow{DB},\overrightarrow{BC}\right)=180^\circ-\left(\overrightarrow{BD},\overrightarrow{BC}\right)=180^\circ-45^\circ=135^\circ$.
			\itemch \textbf{Sai}. Ta có
			\begin{align*}
				\left| \overrightarrow{AB} + \overrightarrow{B'C} + \overrightarrow{AA'} \right|
				&=\left| \overrightarrow{AB} + \overrightarrow{B'C} + \overrightarrow{CC'} \right|\\
				&=\left| \overrightarrow{AB} + \overrightarrow{B'C'} \right|\\
				&=\left| \overrightarrow{AB} + \overrightarrow{BC}\right|\\
				&=\left| \overrightarrow{AC}\right|\\
