			\itemch \textbf{Đúng}. Ta có 
			\begin{eqnarray*}
				\sqrt[3]{\dfrac{2}{5}\cdot \sqrt[7]{\dfrac{2}{5}\cdot \sqrt[3]{\dfrac{2}{5}} } } &=& \sqrt[3]{\dfrac{2}{5}\cdot \sqrt[7]{\dfrac{2}{5}\cdot \left( \dfrac{2}{5} \right)^{\tfrac{1}{3}} } }
				= \sqrt[3]{\dfrac{2}{5}\cdot \sqrt[7]{\left( \dfrac{2}{5}\right)^{\tfrac{4}{3}} } }\\
				&=& \sqrt[3]{\dfrac{2}{5}\cdot \left(\dfrac{2}{5} \right)^{\tfrac{4}{21}} }
				=\sqrt[3]{\left(\dfrac{2}{5} \right)^{\tfrac{25}{21}}}
				=\left(\dfrac{2}{5} \right)^{\tfrac{25}{63}}.
			\end{eqnarray*}
			Suy ra $a=25$ và $b=63$. Dó đó $a+b=88$.

			\itemch \textbf{Sai}. Ta có $$Q = 2\log_{16} x^4 + \log_2 x^2= 2 \log_{2^4}x^4+2\log_2 x=2\log_2 x+2\log_2 x= 4\log_2 x=4\cdot 2=8.$$
		\end{itemchoice}
	}
\end{ex}

