	Cho biểu thức $M=2^{\log_25}+\ln\mathrm{e}^2$ và hàm số $f(x)=\log_3(2x-1)$. %Khi đó 
	\choiceTF
	{Giá trị của biểu thức $M=\dfrac{5}{2}$}
	{\True $f(2)+f(3)=\log_315$}
	{Tập xác định của hàm số $f(x)$ là $\mathscr{D}=\left[\dfrac{1}{2};+\infty\right)$}
	{\True Phương trình $f(x)=2$ có nghiệm là $x=5$}
	\loigiai
	{
		\begin{itemchoice}
			\itemch Sai.\\ Ta có $M=2^{\log_25}+\ln\mathrm{e}^2=5+2\ln\mathrm{e}=5+2\cdot1=7$.
			\itemch Đúng.\\ Ta có $\heva{ & f(2)=\log_3(2\cdot2-1)=\log_33\\ & f(3)=\log_3(2\cdot3-1)=\log_35} \Rightarrow f(2)+f(3)=\log_33+\log_35 =\log_315$.
			\itemch Sai.\\ Để hàm số xác định thì $2x-1>0 \Leftrightarrow x>\dfrac{1}{2}$. \\
			Vậy tập xác định của hàm số $f(x)$ là $\mathscr{D}=\left(\dfrac{1}{2};+\infty\right)$.
			\itemch Đúng.\\ Ta có $f(x)=2 \Leftrightarrow \log_3(2x-1)=2 \Leftrightarrow 2x-1=3^2=9 \Leftrightarrow x=5$.
		\end{itemchoice}
	}
\end{ex}

