\begin{ex}%[1H8H2-2]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Nguyễn Ngọc Huy Trường]
	Cho hình chóp $S.ABCD$ có $SA\perp \left(ABCD\right)$, $ABCD$ là hình vuông cạnh $a$, $SA=a\sqrt{3}$. Điểm $E$ thuộc cạnh $SB$ sao cho $AE\perp EC$.
	\choiceTF
	{\True $BC\perp SA$}
	{Tam giác $SAD$ vuông cân tại $S$}
	{$AC\perp \left(SBD\right)$}
	{\True Tỉ số $\dfrac{SE}{SB}=0{,}75$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=0.7, font=\footnotesize,line join=round, line cap=round, >=stealth]
				\path 
				(0,0) coordinate (A)
				++(-140:2) coordinate (B)
				++(0:3.5) coordinate (C)
				($(A)+(C)-(B)$) coordinate (D)
				($(A)+(0,3)$) coordinate (S)
				($(S)!3/4!(B)$) coordinate (E)
				;
				\foreach \i in{B,C,D}{\draw (S)--(\i);};
				\draw (B)--(C)--(D) (C)--(E);
				\draw[dashed] (S)--(A)--(B) (E)--(A)--(D);
				\pic[draw,angle eccentricity=1.8,angle radius=2mm]{right angle=S--A--D};
				\pic[draw,angle eccentricity=1.8,angle radius=2mm]{right angle=C--E--A};
				\foreach \i/\g in {A/140,B/-90,C/-90,D/-90,S/90,E/160}
				\fill[black] (\i) circle(1pt)+(\g:3mm)node[scale=1]{$\i$};
			\end{tikzpicture}
		\end{center}
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Ta có $SA\perp (ABCD)$ suy ra $SA\perp BC$.
			\itemch \textbf{Sai}. Ta có $SA\perp (ABCD)$ suy ra $SA\perp AD$. Do đó $\triangle SAD$ vuông tại $A$.
			\itemch \textbf{Sai}. Giả sử $AC\perp (SBD)$ suy ra $AC\perp SB$. \\
			Ta có $BC\perp AB$, $BC\perp SA$ suy ra $BC\perp (SAB)$. Do đó $BC\perp SB$. \\
			Mà $AC\perp SB$ nên $SB\perp (ABCD)$ (vô lý). \\
			Vậy $AC$ không vuông góc với $(SBD)$.
			\itemch \textbf{Đúng}. Ta có $BC\perp (SAB)$, $AE\subset (SAB)$ suy ra $BC\perp AE$. \\
			Mà $AE\perp CE$ nên $AE\perp (SBC)$. Do đó $AE\perp SB$. \\
			Ta có $SB = \sqrt{SA^2 + AB^2} = \sqrt{(a\sqrt{3})^2 + a^2} = 2a$. \\
			Xét tam giác $SAB$ vuông tại $A$ có đường cao $AE$, ta có
			$$\dfrac{SE}{SB} = \dfrac{SE\cdot SB}{SB^2} = \dfrac{SA^2}{SB^2} = \dfrac{(a\sqrt{3})^2}{(2a)^2} = \dfrac{3}{4}=0{,}75.$$
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1D6H1-2]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Nguyễn Ngọc Huy Trường]
	Với $a$, $b$ là các số thực dương và khác $1$; $\alpha$, $\beta$ là số thực bất kì.
	\choiceTF
	{$a^{\alpha}\cdot a^{\beta}=a^{\alpha\cdot\beta}$}
	{\True Hàm số $y=a^x$ có tập xác định là $\mathbb{R}$}
	{$\sqrt[3]{a\sqrt{a^3}}=a^{\tfrac{5}{3}}$}
	{\True $\log _{a^2} a^{10}+\log _{\sqrt[3]b} \left(b^{-2}\right)=-1$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}. Ta có $a^{\alpha}\cdot a^{\beta}= a^{\alpha + \beta}$.
			\itemch \textbf{Đúng}. Hàm số $y=a^x$ có tập xác định là $\mathbb{R}$.
			\itemch \textbf{Sai}. Ta có $\sqrt[3]{a\sqrt{a^3}}=a^{\tfrac{5}{3}} = \sqrt[3]{a\cdot a^{\tfrac{3}{2}}} = \sqrt[3]{a^{\tfrac{5}{2}}} = a^{\tfrac{5}{6}}$.
			\itemch \textbf{Đúng}. Ta có $\log _{a^2} a^{10}+\log _{\sqrt[3]b} \left(b^{-2}\right)= \dfrac{10}{2}\log_a a + (-2)\cdot 3\log_b b = 5 - 6 = -1$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[1D9H2-4]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Đỗ Minh Vũ]
	Khảo sát một nhóm $50$ học sinh nam về các môn thể thao các em thích chơi. Kết quả thấy rằng có $30$ học sinh thích chơi đá bóng, $15$ học sinh vừa thích chơi đá bóng vừa thích chơi bóng rổ, $40$ học sinh thích chơi đá bóng hoặc bóng rổ. Chọn ngẫu nhiên một em học sinh trong nhóm này. Xác suất học sinh này thích chơi bóng rổ bằng bao nhiêu? (\textit{làm tròn kết quả đến hàng phần chục}).
	\shortans[oly]{0,5}
	\loigiai{
		\begin{itemize}
			\item Gọi A là biến cố: "học sinh thích chơi bóng đá".
			\item Gọi B là biến cố: "học sinh thích chơi bóng rổ".
		\end{itemize}
		Khi đó ta có\\
		$n(A)=30$, $n(AB)=15$, $n(A\cup B) = 40$ \\
		Từ $n(A\cup B)=n(A) + n(B)-n(AB) \Rightarrow n(B)=40-30+15=25$.\\
		Vậy xác suất để chọn được một học sinh thích chơi bóng rổ là $P(B)=\dfrac{n(B)}{n(\Omega)}=\dfrac{25}{50}=0{,}5$.
	}
\end{ex}

\begin{ex}%[1D5N2-1]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Đỗ Minh Vũ]
	Thời gian (phút) truy cập internet mỗi buổi tối của một số học sinh được cho trong bảng sau:
	\begin{center}
		\begin{tabular}{|c|c|c|c|c|c|}
			\hline \textbf{Thời gian (phút)} &$[9{,}5;12{,}5)$&$[12{,}5;15{,}5)$&$[15{,}5;18{,}5)$&$[18{,}5;21{,}5)$&$[21{,}5;24{,}5)$\\
			\hline \textbf{Số học sinh} &$3$ & $12$&$15$ &$24$ &$2$ \\
			\hline
		\end{tabular}
	\end{center}
	Tính trung vị của mẫu số liệu ghép nhóm trên.
	\shortans[oly]{$18,1$}
	\loigiai{
		Số học sinh được thống kê $n = 3+12+15+24+2 = 56$ (học sinh).\\
		Gọi $ x_1; x_2; \ldots ; x_{56} $ là thời gian truy cập internet mỗi buổi tối của $56$ học sinh xếp theo thứ tự không giảm.\\
		Do $ x_1,x_2,x_3\in \left[9{,}5 ; 12{,}5 \right) $; $ x_4, \ldots, x_{15} \in \left[ 12{,}5 ; 15{,}5\right) $; $ x_{16}, \ldots, x_{30} \in \left[ 15{,}5 ; 18{,}5\right) $;  $ x_{31}, \ldots, x_{54} \in \left[ 18,5 ; 21{,}5\right) $; $ x_{55}, x_{56} \in \left[ 21{,}5 ; 24{,}5\right) $ nên trung vị của mẫu số liệu $ x_1; x_2; \ldots; x_{56} $ là $ x_{28}\in \left[ 15{,}5 ; 18{,}5\right)$.\\			
		Vậy trung vị của mẫu số liệu ghép nhóm là $$ M_e=15{,}5+\dfrac{\dfrac{56}{2}-15}{15}\cdot(18{,}5-15{,}5) =18{,}1.$$
	}
\end{ex}

\begin{ex}%[1H8H1-3]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Đỗ Minh Vũ]
	\immini{
		Cho hình lăng trụ $ABC.A'B'C'$ có đáy $A B C$ là tam giác đều cạnh a. Điểm $H$ là trung điểm cạnh $B C$, $A' H \perp(ABC)$ và $A' H=\dfrac{3 a}{2}$ (tham khảo hình vẽ). Gọi $\alpha$ là góc giữa hai đường thẳng $C C'$ và $A B$. Khi đó, $\cos \alpha$ bằng $\sqrt{\dfrac{a}{b}}$, $\left( \dfrac{a}{b} \text { là phân số tối giản} \right)$ . Hiệu $a-b$ bằng bao nhiêu?}
	{
		\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
			\def\ac{4} \def\ab{2} \def\h{4.0} \def\gocA{50} 
			\coordinate[label=left:$A$] (A) at (0,0);
			\coordinate[label=right:$C$] (C) at (\ac,0);
			\coordinate[label=below left:$B$] (B) at (-\gocA:\ab);
			\coordinate[label=left:$A'$] (A') at ($(A)+(50:\h)$);
			\coordinate[label=below left:$B'$] (B') at ($(B)-(A)+(A')$);
			\coordinate[label=right:$C'$] (C') at ($(C)-(A)+(A')$);
			\coordinate[label=below:$H$] (H) at ($(B)!0.5!(C)$); % Trung điểm BC
			\draw (A')--(A)--(B)--(C)--(C')--(A')--(B')--(C') (B)--(B');
			\draw[dashed] (A)--(C);
			\draw [dashed] (A')--(H); % Nối A' với H
			\foreach \diem in {A,B,C,A',B',C',H} \fill (\diem)circle(1pt);
		\end{tikzpicture}
	}
	\shortans[oly]{$-13$}
	\loigiai{
		\begin{center}
			\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
				\def\ac{4} \def\ab{2} \def\h{4.0} \def\gocA{50} 
				\coordinate[label=left:$A$] (A) at (0,0);
				\coordinate[label=right:$C$] (C) at (\ac,0);
				\coordinate[label=below left:$B$] (B) at (-\gocA:\ab);
				\coordinate[label=left:$A'$] (A') at ($(A)+(50:\h)$);
				\coordinate[label=below left:$B'$] (B') at ($(B)-(A)+(A')$);
				\coordinate[label=right:$C'$] (C') at ($(C)-(A)+(A')$);
				\coordinate[label=below:$H$] (H) at ($(B)!0.5!(C)$); % Trung điểm BC
				\draw (H)--++(90:0.25)--++(-195  :0.15)--++(-90:0.25);
				\draw (B)--(A')--(A)--(B)--(C)--(C')--(A')--(B')--(C') (B)--(B');
				\draw[dashed] (H)--(A)--(C) (A')--(H);
				%					\draw [dashed] (A')--(H); % Nối A' với H
				\foreach \diem in {A,B,C,A',B',C',H} \fill (\diem)circle(1pt);
			\end{tikzpicture}
		\end{center}
		Do $CC' \parallel AA'$ nên $(CC',AB)=(AA',AB)$.\\
		Ta có\\
		$AB=a$, $AA'=\sqrt{A'H^2+AH^2}=\sqrt{\dfrac{9a^2}{4}+\dfrac{3a^2}{4}}=a\sqrt{3}.$\\ $A'B=\sqrt{A'H^2+BH^2}=\sqrt{\dfrac{9a^2}{4}+\dfrac{a^2}{4}}=\dfrac{a\sqrt{10}}{2}$.\\
		Áp dụng định lí cosin trong tam giác $AA'B$ có\\
		$\cos \widehat{BAA'}=\dfrac{AB^2+AA'^2-A'B^2}{2\cdot AB\cdot AA'}=\dfrac{a^2+3a^2-\frac{10a^2}{4}}{2 \cdot a\cdot a\sqrt{3}}=\sqrt{\dfrac{3}{16}} > 0. \\
		\Rightarrow \cos(CC',AB)=\cos \widehat{BAA'} = \sqrt{\dfrac{3}{16}} \Rightarrow a - b = 3 - 16 = -13$.
	}
\end{ex}

\begin{ex}%[1D6V2-4]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Đỗ Minh Vũ]
	Năm $2020$, một hãng ôtô niêm yết giá xe X là $860$ triệu đồng và dự định trong $10$ năm tiếp theo mỗi năm giảm $2 \%$ giá bán so với năm trước. Vào năm $2025$, một khách hàng mua xe X của hãng. Biết rằng để xe có thể hợp pháp lưu thông trên đường, ngoài số tiền bỏ ra để mua xe theo giá niêm yết của hãng, khách hàng còn phải trả thêm các khoản phí bằng khoảng $15 \%$ giá niêm yết của xe. Nếu hãng ôtô thực hiện niêm yết giá xe đúng như dự định ban đầu thì tổng cộng khách hàng này phải bỏ ra bao nhiêu triệu đồng để sở hữu chiếc xe X hợp pháp lưu thông trên đường? (\textit{Làm tròn kết quả đến hàng đơn vị}).
	\shortans[oly]{$894$}
	\loigiai{
		Xét công thức lãi kép $A_n=A_0\left(1+r\right)^n$.\\
		Ở đây, giá bán xe năm 2020 là $A_0 = 860 $ (triệu đồng).\\
		Mỗi năm giá xe giảm $2\%$ giá bán, nghĩa là $ r = -2\%$ và $n=5$.\\
		$\Rightarrow A_5 = A_0(1-0{,}2\%)^5 \approx 777{,}37188$ (triệu đồng).\\
		Vậy số tiền để khách hàng phải trả để sở hữu xe X hợp pháp (cộng thêm $15\%$ các khoản phí) là\\
		$ A = A_5 \cdot 115\%  \approx 894$ (triệu đồng).
	}
\end{ex}

\begin{ex} %[1D6H1-1]%[Dự án tex đề GHKII THPT Lê Quý Đôn - Ninh Thuận- 24-25][Đỗ Minh Vũ]
	Cho $a$ là số thực dương và $a \neq \pm 1$. Rút gọn biểu thức$\colon$
	$$A=\frac{2 \sqrt{a}}{\left(a+2 a^{\frac{1}{2}}+1\right)(\sqrt{a}-1)}\colon  \dfrac{a^{\frac{1}{2}}}{a^{\frac{1}{2}}+1}$$
	\loigiai{
		$A=\frac{2 \sqrt{a}}{\left(a+2 a^{\frac{1}{2}}+1\right)(\sqrt{a}-1)}\colon  \frac{a^{\frac{1}{2}}}{a^{\frac{1}{2}}+1}=\frac{2 \sqrt{a}}{\left(a+2\sqrt{a}+1\right)(\sqrt{a}-1)}\cdot \frac{\sqrt{a}+1}{ \sqrt{a}}=\frac{2}{(\sqrt{a}+1)(\sqrt{a}-1)}=\frac{2}{a-1}$
}
\end{ex}

