	Cho hàm số $f(x)=\sqrt{\sin 3x}$ và hàm số $g(x)=\dfrac{2x^2+4}{x-1}$.
	\choiceTF
	{$f^{\prime}(x)=\dfrac{\cos 3x}{2\sqrt{\sin 3x}}$}
	{\True $g^{\prime}(x)=\dfrac{2x^2-4x-4}{(x-1)^2}$}
	{\True $f^{\prime}\left(\dfrac{\pi}{6}\right)+g^{\prime}(2)=-4$}
	{Đặt $h(x)=2\sqrt{\sin 3x}\cdot f^{\prime}(x)+(x-1)\cdot g(x)$. Khi đó $h^{\prime \prime}(0)=-32$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai.} Ta có $f^{\prime}(x)=\dfrac{\left(\sin 3x\right)^{\prime}}{2\sqrt{\sin 3x}}=\dfrac{3\cos 3x}{2\sqrt{\sin 3x}}$.
			\itemch \textbf{Đúng.} Ta có $g^{\prime}(x)=\dfrac{\left(2x^2+4\right)^{\prime}\cdot \left(x-1\right)-\left(2x^2+4\right)\cdot \left(x-1\right)^{\prime}}{\left(x-1\right)^2}=\dfrac{4x\left(x-1\right)-\left(2x^2+4\right)}{\left(x-1\right)^2}$.\\
			Suy ra $g^{\prime}(x)=\dfrac{2x^2-4x-4}{\left(x-1\right)^2}$.
			\itemch \textbf{Đúng.} Ta có $f^{\prime}\left(\dfrac{\pi}{6}\right)=\dfrac{3\cos \dfrac{3\pi}{6}}{2\sqrt{\sin \dfrac{3\pi}{6}}}=0$, $g^{\prime}(2)=\dfrac{2\cdot 2^2-4\cdot 2-4}{(2-1)^2}=-4$.\\
			Do đó $f^{\prime}\left(\dfrac{\pi}{6}\right)+g^{\prime}(2)=-4$.
			\itemch \textbf{Sai.} Ta có $h(x)=2\sqrt{\sin 3x}\cdot f^{\prime}(x)+(x-1)\cdot g(x)=3\cos 3x+2x^2+4$.\\
			Suy ra $h^{\prime}(x)=-9\sin 3x +4x \Rightarrow h^{\prime \prime}(x)=-27\cos 3x+4$.\\
			Do đó $h^{\prime \prime}(0)=-27\cos 0+4=-23$.
		\end{itemchoice}
	}
\end{ex}

