		\begin{itemchoice}
			\itemch Ta có
			\[F(1) - F(0)=\displaystyle \int \limits_0^1 f(x) \mathrm{\,d}x=-\dfrac{7}{2}.\]
			\itemch Ta có
			\[\displaystyle \int \limits_0^1 f(x) \mathrm{\,d}x + \int \limits_1^2 f(x) \mathrm{\,d}x=\int \limits_0^2 f(x) \mathrm{\,d}x \Leftrightarrow -\dfrac{7}{2}+ \int \limits_1^2 f(x) \mathrm{\,d}x=-2 \Leftrightarrow \int \limits_1^2 f(x) \mathrm{\,d}x =\dfrac{3}{2}.\]
			\itemch Ta có
			\[\displaystyle \int f(x) \mathrm{\,d}x = \int \left(a x^2 + b x + c\right) \mathrm{\,d}x = \dfrac{a}{3} x^3 + \dfrac{b}{2} x^2 + c x+C.\]
			\itemch Ta có 
			\begin{itemize}
				\item $\displaystyle \int \limits_0^1 f(x)  \mathrm{\,d}x=\left(\dfrac{a}{3} x^3 + \dfrac{b}{2} x^2 + c x\right)\Bigg|^1_0=\dfrac{a}{3}+\dfrac{b}{2}+c=-\dfrac{7}{2}$.
				\item $\displaystyle \int \limits_0^2 f(x)  \mathrm{\,d}x=\left(\dfrac{a}{3} x^3 + \dfrac{b}{2} x^2 + c x\right)\Bigg|^2_0=\dfrac{8a}{3}+2b+2c=-2$.
				\item $\displaystyle \int \limits_0^3 f(x)  \mathrm{\,d}x=\left(\dfrac{a}{3} x^3 + \dfrac{b}{2} x^2 + c x\right)\Bigg|^3_0=9a+\dfrac{9b}{2}+3c=\dfrac{13}{2}$.
			\end{itemize}
			Suy ra $a=1$, $b=3$, $c=-\dfrac{16}{3}$.\\
			Nên $a+b+3c=1+3+3\cdot \left(-\dfrac{16}{3}\right)=-12$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D4H2-2]
	Cho hàm số $f(x)=2x+1$.
	\choiceTF
	{\True $\displaystyle \int f(x)\mathrm{\,d}x = x^2 + x + C$}
	{$\displaystyle \int \limits_0^1 (x - 1) f(x)\mathrm{\,d}x = \dfrac{2}{3}$}
	{\True Nếu $G(x)$ là một nguyên hàm của $f(x)$ với $G(2) = 5$ thì $G(x) = x^2 + x - 1$}
	{Gọi $F(x)$ là một nguyên hàm của $f(x)$, biết $F(1) = 2$ và $ \dfrac{1}{F(1)} + \dfrac{1}{F(2)} + \cdots + \dfrac{1}{F(100)} = \dfrac{a}{b}$ với $a$, $b \in \mathbb{N}$, $\dfrac{a}{b}$ tối giản thì $a + b = 201$}
	\loigiai{
		\begin{itemchoice}
			\itemch Ta có
			\[\displaystyle \int f(x)\mathrm{\,d}x =\int (2x+1)\mathrm{\,d}x = x^2 + x + C.\]
			\itemch Ta có
			\begin{eqnarray*}
				\displaystyle \int \limits_0^1(x-1)f(x)\mathrm{\,d}x &=&\int \limits_0^1(x-1)(2x+1)\mathrm{\,d}x =\int \limits_0^1 (2x^2-x-1)\mathrm{\,d}x \\
				&=& \left(\dfrac{2}{3}x^3-\dfrac{1}{2}x^2-x\right) \Bigg|_0^1=\left(\dfrac{2}{3}-\dfrac{1}{2}-1\right) -0=-\dfrac{5}{6}.
			\end{eqnarray*}
			\itemch $G(x)$ là một nguyên hàm của $f(x)$ nên $G(x)=x^2+x+C$.\\
			Lại có $G(2)=2^2+2+C=5$ nên $C=-1$.\\
			Vậy $G(x)=x^2+x-1$.
			\itemch $F(x)$ là một nguyên hàm của $f(x)$ nên $F(x)=x^2+x+C$.\\
			Lại có $F(1)=1^2+1+C=2$ nên $C=0$.\\
			Vậy $F(x)=x^2+x=x(x+1)$.\\
			Ta có $\dfrac{1}{F(x)} =\dfrac{1}{x(x+1)} =\dfrac{1}{x}-\dfrac{1}{x+1}$.\\
			Suy ra 
			\[\dfrac{1}{F(1)} + \dfrac{1}{F(2)} + \cdots + \dfrac{1}{F(100)} = \dfrac{1}{1}-
			\dfrac{1}{2}+\dfrac{1}{2}-\dfrac{1}{3}+\cdots+\dfrac{1}{99}-\dfrac{1}{100}=1 -\dfrac{1}{100}=\dfrac{99}{100}.\]
			Vậy $a=99$, $b=100$ nên $a+b=199$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D4H2-2]
	Trong không gian $Oxyz$, cho mặt phẳng $(P)$ có phương trình $2x + y + 2z - 3 = 0$.
	\choiceTF
	{\True Mặt phẳng $(P)$ có một vectơ pháp tuyến là $\overrightarrow{n} = (2; 1; 2)$}
	{ Điểm $M(1; -1; -1)$ thuộc mặt phẳng $(P)$}
	{Phương trình mặt phẳng $(Q)$ đi qua $A(2; 3; -1)$ và song song với mặt phẳng $(P)$ là $2x + y + 2z - 7 = 0$}
	{\True Mặt phẳng $(\alpha)$ chứa trục $Ox$ và vuông góc với $(P)$ có phương trình dạng $ax + by - 2z + d = 0$, khi đó $T = a - 3b + d = -12$}
	\loigiai{
		\begin{itemchoice}
			\itemch Phương trình mặt phẳng $(P) \colon 2x+y+2z-3=0$ nên vectơ pháp tuyến của mặt phẳng $(P)$ là $\overrightarrow{n}=(2;12)$.
			\itemch Thay tọa độ điểm $M(1; -1; -1)$ vào phương trình mặt phẳng, ta được 
			\[2 \cdot 1 + (-1) + 2 \cdot (-1)  = -1 \ne 3\]
			nên $M$ không thuộc mặt phẳng.
			\itemch Vì $(P) \parallel (Q)$ nên $\overrightarrow{n}_{(P)}=\overrightarrow{n}_{(Q)}=(2;1;2)$.\\
			Phương trình mặt phẳng $(Q)$ là
			\[2 \cdot (x-2)+1  \cdot (y-3) +2 \cdot (z+1)=0 \Leftrightarrow 2x+y+2z-5=0.\]
			\itemch Vì mặt phẳng $(\alpha)$ chứa trục $Ox$ và vuông góc với mặt phẳng $(P)$ nên $\overrightarrow{i}$, $\overrightarrow{n}_{(P)}$ là cặp vectơ chỉ phương của mặt phẳng $(\alpha)$.\\
			Suy ra $\overrightarrow{n}_{(\alpha)}=\left[\overrightarrow{i},\overrightarrow{n}_{(P)}\right]=(0;-2;1)$.\\
			Lại có mặt phẳng $(\alpha)$ đi qua gốc tọa độ $O$ nên phương trình mặt phẳng $(\alpha)$ là
			\[0 \cdot (x-0)-2(y-0)+1 \cdot (z-0)= 0\Leftrightarrow -2y+z=0 \Leftrightarrow4y-2z=0.\]
			Vậy $a=0$, $b=4$, $d=0$ nên $a-3b+d=0-3\cdot4+0=-12$.
	\end{itemchoice}}
\end{ex}

\begin{ex}%[2D4H2-3]
	Cho hàm số $f(x) = 2x - 3\cos x$.
	\choiceTF
	{Một nguyên hàm của $f(x)$ là $g(x) = x^2 + 3\sin x + 2$}
	{Nếu $F(x)$ là một nguyên hàm của $f(x)$ và $F\left(\dfrac{\pi}{2}\right) = 3$ thì $F(x) = x^2 - 3\sin x + 6$}
	{\True Nguyên hàm $F(x)$ của $f(x)$ thỏa mãn điều kiện $F(0) = 0$ là $F(x) = x^2 - 3\sin x$}
	{\True $\displaystyle \int f(x + \pi)\,\mathrm{\,d}x = x^2 + 3\sin x + 2(\pi + C)$, $C$ là hằng số}
	\loigiai{
		Ta có nguyên hàm của $f(x)$ là $\displaystyle \int f(x)\mathrm{\,d}x = \int (2x - 3\cos x)\mathrm{\,d}x = x^2 - 3\sin x + C$.
		\begin{itemchoice}
			\itemch  $g(x) = x^2 + 3\sin x + 2$ không phải là một nguyên hàm của $f(x)$.
			\itemch $F(x)$ là một nguyên hàm của $f(x)$ nên $F(x) = x^2 - 3\sin x + C$. Ta có 
			\[F\left(\dfrac{\pi}{2}\right)=\left(\dfrac{\pi}{2}\right)^2-3 \sin \dfrac{\pi}{2}+C=\dfrac{\pi^2}{4}-3+C=3 \Leftrightarrow C=6-\dfrac{\pi^2}{4}.\]
			Suy ra $F(x)=x^2 -3\sin x +6 -\dfrac{\pi^2}{4}$.
			\itemch $F(x)$ là một nguyên hàm của $f(x)$ nên $F(x) = x^2 - 3\sin x + C$. Ta có 
			\[F(0)=0^2-3 \sin 0+C=C=0.\]
			Suy ra $F(x)=x^2 -3\sin x$.
			\itemch Ta có
			\[\displaystyle \int f(x+\pi) \mathrm{\,d} x=\int 2(x+\pi) -3 \cos (x +\pi)  \mathrm{\,d}x=\int 2x+3\cos x +2\pi  \mathrm{\,d} x=x^2+3\sin x +2\pi x +C.\]
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D4H3-1]
	Tính diện tích hình phẳng giới hạn bởi đồ thị các hàm số $y=-x^2+2x+1$, $y=2x^2-4x+1$.
	\loigiai
	{
		Xét phương trình hoành độ giao điểm:
		\begin{eqnarray*}
			& -x^2+2x+1 = 2x^2 - 4x + 1 \\
			& \Leftrightarrow -3x^2 + 6x = 0 \Leftrightarrow x( -3x + 6 ) = 0 \Leftrightarrow x=0 \text{ hoặc } x=2
		\end{eqnarray*}
		Diện tích hình phẳng cần tìm là:
		\begin{eqnarray*}
			& S = \int\limits_0^2 \left[(-x^2+2x+1) - (2x^2 - 4x + 1)\right] \mathrm{\,d}x = \int\limits_0^2 (-3x^2 +6x) \mathrm{\,d}x \\
			& = \left[-x^3 + 3x^2\right]_0^2 = \left(-8 + 12\right) - 0 = 4
		\end{eqnarray*}
		Vậy diện tích là $4$ (đơn vị diện tích).
	}
\end{ex}

\begin{ex}%[2D4H3-3]
	Gọi $V$ là thể tích khối tròn xoay tạo thành khi quay hình phẳng giới hạn bởi các đường $y=\sqrt{x}$, $y=0$, $x=4$ quanh trục $Ox$. Tính $V$ (làm tròn đến hàng phần mười).
	\loigiai
	{
		Thể tích khối tròn xoay:
		\begin{eqnarray*}
			& V = \pi \int\limits_0^4 (y^2)\mathrm{\,d}x = \pi \int\limits_0^4 x\mathrm{\,d}x = \pi \left[\dfrac{x^2}{2}\right]_0^4 = \pi \cdot \dfrac{16}{2} = 8\pi
		\end{eqnarray*}
		Làm tròn đến hàng phần mười: $V \approx 25{,}1$ (đơn vị thể tích).
	}
\end{ex}

\begin{ex}%[2D4V3-2]
	Ông An muốn làm một cái cổng hình Parabol như hình vẽ. Chiều cao $GH=4$ m, chiều rộng $AB=4$ m, $AC=BD=0{,}9$ m. Ông An làm hai cánh cổng chính đóng lại là hình chữ nhật $CDEF$ tô đậm có giá là $1\,200\,000$ đồng/m$^2$, còn các phần để trắng làm xiên hoa có giá là $900\,000$ đồng/m$^2$. Hỏi để làm hai phần nói trên ông An phải trả bao nhiêu triệu đồng (làm tròn đến hàng phần mười)?
	\loigiai
	{
		Gọi trục đối xứng là $Ox$, chọn hệ trục sao cho đỉnh parabol $O(0;0)$, khi đó parabol có dạng $y=ax^2$.\\
		Vì $G(2;4)$ thuộc đồ thị nên $4=a\cdot 4 \Rightarrow a=1$, nên $y=x^2$.\\
		Góc $GAB$ là một hình chữ nhật nằm trong parabol từ $x=-2$ đến $x=2$, nên diện tích phần parabol là:
		\begin{eqnarray*}
			& S_1 = \int\limits_{-2}^2 x^2\mathrm{\,d}x = \left[\dfrac{x^3}{3}\right]_{-2}^2 = \dfrac{8}{3} - \left(-\dfrac{8}{3}\right) = \dfrac{16}{3}
		\end{eqnarray*}
		Diện tích hình chữ nhật $CDEF$: $S_2 = AB \cdot AC = 4 \cdot 0{,}9 = 3{,}6$\\
		Phần xiên hoa là $S_1 - S_2 = \dfrac{16}{3} - 3{,}6 \approx 5{,}73 - 3{,}6 = 2{,}13$\\
		Chi phí:
		\begin{eqnarray*}
			& C = 3{,}6 \cdot 1\,200\,000 + 2{,}13 \cdot 900\,000 = 4\,320\,000 + 1\,917\,000 = 6\,237\,000 (\text{ đồng})\\
			& \Rightarrow \text{Tính ra triệu đồng: } \dfrac{6\,237\,000}{10^6} = 6{,}2 \text{ (triệu đồng)}
		\end{eqnarray*}
	}
\end{ex}

