\begin{ex}%[Lớp 12 - Đề giữa học kì 2 - THPT Chuyên Lê Quý Đôn - Ninh Thuận]%[Tư Đô Nguyên]%[2D4V2-6]
	Một loại thuốc $A$ được tiêm vào bệnh nhân, nồng độ (đơn vị: mg/l) của thuốc trong máu sau $x$ phút (kể từ khi bắt đầu tiêm) được xác định bởi công thức $f(x)=\dfrac{30x}{x^2+4}$. Để đưa ra lời khuyên và cách xử lí phù hợp cho bệnh nhân, người ta cần tính toán một số yếu tố về nồng độ của thuốc trong máu.
	\choiceTF
	{Sau $10$ ngày thì nồng độ thuốc $A$ trong máu nhỏ hơn $0{,}002$ mg/l}
	{\True Nồng độ thuốc trong máu đạt giá trị lớn nhất là $7{,}5$\,mg/l tại thời điểm $2$ phút sau khi tiêm}
	{$F(x)=10\ln \left(x^2+4\right)$ là một nguyên hàm của $f(x)$}
	{\True Nồng độ trung bình của thuốc $A$ (làm tròn đến hàng phần trăm) trong khoảng thời gian $30$ phút từ khi bắt đầu tiêm là $2{,}71$\,mg/l}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Ta có $10$ ngày = $14\,400$ phút.\\
			Ta tính $f(14\,400)=f(14400)=\dfrac{30 \cdot 14\,400}{14\,400^2+4}\approx 0{,}002083>0{,}002$.
			\itemch \textbf{Đúng}.\\
			Xét hàm số $f(x)=\dfrac{30x}{x^2+4}$ có $f'(x)=\dfrac{30(-x^2+4)}{\left(x^2+4\right)^2}$.\\
			Ta có $f'(x)=0 \Leftrightarrow -x^2+4=0 \Leftrightarrow \hoac{&x=2 \\ &x=-2.}$\\
			Do $x>0$ nên ta nhận $x=2$, khi đó ta có bảng biến thiên sau
			\begin{center}
				\begin{tikzpicture}[font=\normalsize,t style/.style={style=solid}]
					%dòng khai báo
					\tkzTabInit[nocadre=true,lgt=1.2,espcl=2.5,deltacl=0.5]
					{$x$/0.75,$f'(x)$/0.75, $f(x)$/2}
					{$0$, $2$, $+\infty$}
					%dòng xét dấu của đạo hàm
					\tkzTabLine{,+, 0,-,} % z, t, d, h (h: tô miền);
					%Dòng biến thiên f(x)
					\tkzTabVar{-/{$0$}, +/{$7{,}5$}, -/{$0$}}
				\end{tikzpicture}
			\end{center}
			Vậy nồng độ thuốc trong máu đạt giá trị lớn nhất là $7{,}5$\,mg/l tại thời điểm $2$ phút sau khi tiêm.
			\itemch \textbf{Sai}.\\
			Xét $F(x)=10\ln \left(x^2+4\right)$, ta tính đạo hàm
			\[
			F'(x)=\left[10\ln (x^2+4)\right]=\dfrac{20x}{x^2+4} \ne \dfrac{30x}{x^2+4} = f(x).
			\]
			\itemch \textbf{Đúng}.\\
			Ta có $A=\dfrac{1}{30-0}\displaystyle\int\limits_{0}^{30} f(x) \mathrm{\,d}x \approx 2{,}71$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[Lớp 12 - Đề giữa học kì 2 - THPT Chuyên Lê Quý Đôn - Ninh Thuận]%[Tư Đô Nguyên]%[2D4V2-2]
	\immini{
		Cho hàm số $y=f(x)$ có đồ thị như hình bên.
		\choiceTF
		{Diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục $Ox$ và hai đường thẳng $x=3$, $x=5$ tính bởi công thức là $\displaystyle\int\limits_3^5f(x)\mathrm{\,d}x$}
		{Diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục $Ox$ và hai đường thẳng $x=0$, $x=2$ là $2$}
		{\True $\displaystyle\int\limits_1^2f(t)\mathrm{\,d}t=2$}
		{Biết rằng $f(x)$ là một hàm số bậc ba khi $x \in[2; 5]$. Khi đó $\displaystyle\int\limits_0^5f(x) \mathrm{\,d}x=-\dfrac{9}{4}$}
	}
	{
		\begin{tikzpicture}[line join=round, line cap=round, >=stealth, font=\footnotesize, scale=.8]
			% Lưới
			\draw[step=1, gray, very thin] (-0.5,-3) grid (5.5,4);

			% Trục toạ độ
			\draw[->, thick] (-0.5,0)--(5.5,0) node[below]{$x$};
			\draw[->, thick] (0,-3)--(0,4) node[below left]{$y$};

			% Đánh dấu điểm -2 và 0 trên trục Oy
			\filldraw[gray] (0,-2) circle (1pt);
			\filldraw[gray] (0,0) circle (1pt);

			% Các đoạn đồ thị
			\draw[blue, thick] (0,0) -- (1,2);                 % đoạn thẳng tăng
			\draw[blue, thick] (1,2) -- (2,2);                 % đoạn hàm hằng y=2
			\draw[blue, thick, domain=2:3, samples=100, smooth]
			plot(\x, {-2*(\x - 2)^2 + 2});                   % đoạn parabol ngửa từ (2,2) đến (3,-2)
			\draw[blue, thick, domain=3:5.2, samples=200, smooth]
			plot(\x, {\x^3 - 9*\x^2 + 24*\x - 18});          % đoạn hàm bậc ba y = x^3 - 9x^2 + 24x - 18

			% Đánh dấu trục
			\foreach \x in {1,2,3,4,5}
			\draw (\x,0.1)--(\x,-0.1) node[below left] {\x};
			\foreach \y in {-2,-1,1,2}
			\draw (0.1,\y)--(-0.1,\y) node[above left] {\y};
		\end{tikzpicture}
	}

	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai}.\\
			Diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục $Ox$ và hai đường thẳng $x=3$, $x=5$ tính bởi công thức là $\displaystyle\int\limits_3^5|f(x)|\mathrm{\,d}x$.
			\itemch \textbf{Sai}.\\
			Diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục $Ox$ và hai đường thẳng $x=0$, $x=2$ bằng
			\[
			S_1=\dfrac{1}{2}\cdot 1 \cdot 2 + 2 \cdot 1 = 3.
			\]
			\itemch \textbf{Đúng}.\\
			Ta có trên đoạn $[1;2]$ thì $f(x)=2$ nên
			\[
			\displaystyle\int\limits_1^2f(t)\mathrm{\,d}t=\displaystyle\int\limits_1^2f(x)\mathrm{\,d}x=\displaystyle\int\limits_1^2 2\mathrm{\,d}x=(2-1)\cdot 2=2.
			\]
			\itemch \textbf{Sai}.\\
			Trên đoạn $[2;5]$, $f(x)$ là hàm số bậc ba nên có dạng $f(x)=ax^3+bx^2+cx+d$ có đồ thị $(P)$. \\
			Ta có
			\[
			\heva{
				& (2;2) \in (P) \\
				& (3;0) \in (P) \\
				& (4;-2) \in (P) \\
				& (5;2) \in (P)
			} \Leftrightarrow
			\heva{
				& 8a+4b+2c+d=2 \\
				& 27a+9b+3c+d=0 \\
				& 64a+16b+4c+d=-2 \\
				& 125a+25b+5c+d=2
			} \Leftrightarrow
			\heva{
				& a= 1 \\
				& b=-9 \\
				& c=24 \\
				& d=-18.
			}
			\]
			Khi đó $f(x)=x^3-9x^2+24x-18$ trên đoạn $[2;5]$.\\
			Ta có:
			\[
			\displaystyle\int\limits_0^5 f(x)\mathrm{\,d}x=\displaystyle\int\limits_0^2 f(x)\mathrm{\,d}x+\displaystyle\int\limits_2^5 f(x)\mathrm{\,d}x=3+\displaystyle\int\limits_2^5 \left(x^3-9x^2+24x-18\right)\mathrm{\,d}x=\dfrac{9}{4}.
			\]
		\end{itemchoice}
	}
