\begin{ex}%[2D4H3-1]%[Dự án đề kiểm tra giữa kì khối 11 GKII NH24-25-Dot 8-TheHung Nguyen]%[THPT DTNT-PHÚ YÊN]
	Diện tích hình phẳng giới hạn bởi các đường cong $y = x^3 - 6x$ và $y = x^2$ bằng
	\choice
	{$\dfrac{63}{4}$}
	{\True $\dfrac{253}{12}$}
	{$\dfrac{125}{12}$}
	{$\dfrac{16}{3}$}
	\loigiai{
		Phương trình hoành độ giao điểm của hai đường cong
		$$x^3 - 6x = x^2 \Leftrightarrow x^3 - x^2 - 6x = 0 \hoac{&x=-2\\&x=0\\&x=3.}$$	
		Xét dấu $g(x) = x^3 - x^2 - 6x$ trên các khoảng $(-2; 0)$ và $(0; 3)$.
		\begin{itemize}
			\item Trên $(-2; 0)$, chọn $x=-1$, khi đó $g(-1) = (-1)^3 - (-1)^2 - 6(-1) = 4 > 0$.
			\item Trên $(0; 3)$, chọn $x=1$, khi đó $g(1) = 1^3 - 1^2 - 6(1) = -6 < 0$.
		\end{itemize}
		Do đó ta có
		\begin{eqnarray*}
			S&=&\displaystyle\int_{-2}^{3} \left|x^3 - 6x - x^2\right| \mathrm{\,d}x\\
			S&=& \displaystyle\int_{-2}^{0} (x^3 - x^2 - 6x) \mathrm{\,d}x + \int_{0}^{3} -(x^3 - x^2 - 6x) \mathrm{\,d}x\\
			&= & \left.\left(\dfrac{x^4}{4} - \dfrac{x^3}{3} - 3x^2\right)\right|_{-2}^0+ \left.\left(-\dfrac{x^4}{4} + \dfrac{x^3}{3} + 3x^2\right)\right|_{0}^{3}\\
			&= & \dfrac{16}{3}+\dfrac{63}{4}\\
			&=&\dfrac{253}{12}.
		\end{eqnarray*}	
	}
\end{ex}

\begin{ex}%[2D4V2-2]%[Dự án đề kiểm tra giữa kì khối 11 GKII NH24-25-Dot 8-TheHung Nguyen]%[THPT DTNT-PHÚ YÊN]
	Cho hàm số $f(x) = 4x^3 + x$ liên tục trên $\mathbb{R}$. $F(x)$ là một nguyên hàm của $f(x)$. Các phát biểu sau đây \textbf{đúng} hay \textbf{sai}?
	\choiceTFt  
	{\True $\displaystyle\int_{3}^{5} f(x) \mathrm{\,d}x = 552$} 
	{\True Hàm số có một nguyên hàm là $F(x) = x^4 + \dfrac{x^2}{2} - 2025$} 
	{Biết $F(1) = 4$ khi đó $F(2) = \dfrac{31}{2}$} 
	{\True $\displaystyle\int_{-2}^{1} |f(x)| \mathrm{\,d}x = \dfrac{39}{2}$} 
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}. Ta có $\displaystyle\int_{3}^{5} (4x^3 + x) \mathrm{\,d}x = \left.\left(x^4 + \dfrac{x^2}{2}\right)\right|_{3}^{5} = \left(5^4 + \dfrac{5^2}{2}\right) - \left(3^4 + \dfrac{3^2}{2}\right)= 552$.
			\itemch \textbf{Đúng}. 
			Ta có $F(x)=\displaystyle\int f(x)\mathrm{\,d}x=\displaystyle\int 4x^3+x\mathrm{\,d}x=x^4+\dfrac{x^2}{2}+C$.\\
			chọn $C=-2025$, nên ta được một nguyên hàm là $F(x) = x^4 + \dfrac{x^2}{2} - 2025$.
			\itemch \textbf{Sai}. Với  $F(1)=4$ ta được  $1^4 + \dfrac{1^2}{2} + C = 4 \Rightarrow C = \dfrac{5}{2}$.\\ Khi đó $F(x) = x^4 + \dfrac{x^2}{2} + \dfrac{5}{2}$.\\
			Do đó $F(2) = 2^4 + \dfrac{2^2}{2} + \dfrac{5}{2} = \dfrac{41}{2} \ne \dfrac{31}{2}$.
			\itemch \textbf{Đúng}. Ta có $f(x)=4x^2+x=x(4x^2+1)$.\\
			Do có $4x^2+1\ge 0\, \forall x\in [-2;1]$ suy ra   $ f(x)\le 0$ trên $[-2;0]$ và $f(x)\ge 0$ trên $[0;1]$.\\
			\begin{eqnarray*}
				\displaystyle\int_{-2}^{1} |f(x)| \mathrm{\,d}x	&=& \int_{-2}^{0} (-4x^3 - x) \mathrm{\,d}x + \int_{0}^{1} (4x^3 + x) \mathrm{\,d}x\\
				&= & \left.\left(-x^4 - \dfrac{x^2}{2}\right)\right|_{-2}^{0} + \left.\left(x^4 + \dfrac{x^2}{2}\right)\right|_{0}^{1}\\
				&= & 18 + \dfrac{3}{2}\\
				&= & \dfrac{39}{2}.
			\end{eqnarray*}	
		\end{itemchoice}
	}
\end{ex}

