\begin{ex}%[2D4H2-4]%[Dự án đề kiểm tra giữa kì khối 11 GKII NH24-25-Dot 8-TheHung Nguyen]%[THPT DTNT-PHÚ YÊN]
	Cho $\displaystyle\int_{-3}^{0} f(x) \mathrm{\,d}x = -4$ và $\displaystyle\int_{-3}^{0} g(x) \mathrm{\,d}x = -3$. Xét tính đúng sai của các mệnh đề sau
	\choiceTFt
	{\True $\displaystyle\int_{-3}^{0} [f(x)+g(x)] \mathrm{\,d}x = -7$} 
	{Nếu $\displaystyle\int_{-3}^{0} [mf(x)+ng(x)] \mathrm{\,d}x = -51$ và $\displaystyle\int_{-3}^{0} [nf(x)+mg(x)] \mathrm{\,d}x = -19$ thì $m+n=-3$} 
	{$\displaystyle\int_{0}^{-3} f(x) \mathrm{\,d}x = -4$}
	{\True $\displaystyle\int_{-3}^{0} -3f(x) \mathrm{\,d}x = 12$} 
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng}. $\displaystyle\int_{-3}^{0} [f(x)+g(x)] \mathrm{\,d}x = \int_{-3}^{0} f(x) \mathrm{\,d}x + \int_{-3}^{0} g(x) \mathrm{\,d}x = (-4) + (-3) = -7$.
			\itemch \textbf{Sai}. Ta có hệ phương trình \\

			\begin{eqnarray*}
				& & \heva{& m\int_{-3}^{0} f(x)\mathrm{\,d}x + n\int_{-3}^{0} g(x)\mathrm{\,d}x = -51 \\& n\int_{-3}^{0} f(x)\mathrm{\,d}x + m\int_{-3}^{0} g(x)\mathrm{\,d}x = -19}\\
				&\Leftrightarrow & \heva{& m(-4) + n(-3) = -51 \\& n(-4) + m(-3) = -19}\\
				&\Leftrightarrow & \heva{& 4m + 3n = 51 \\& 3m + 4n = 19}\\
				&\Rightarrow & 7m+7n=70\\
				&\Rightarrow & m+n=10.
			\end{eqnarray*}		
			\itemch \textbf{Sai}. Ta có $\displaystyle\int_{0}^{-3} f(x) \mathrm{\,d}x = - \int_{-3}^{0} f(x) \mathrm{\,d}x = -(-4) = 4$. 
			\itemch \textbf{Đúng}. $\displaystyle\int_{-3}^{0} -3f(x) \mathrm{\,d}x = -3 \int_{-3}^{0} f(x) \mathrm{\,d}x = -3 \cdot (-4) = 12$.
		\end{itemchoice}			
	}
\end{ex}

\begin{ex}%[2D4H2-4]%[Dự án đề kiểm tra giữa kì khối 11 GKII NH24-25-Dot 8-TheHung Nguyen]%[THPT DTNT-PHÚ YÊN]
	Gọi $F(x)$ là một nguyên hàm của hàm $f(x) = e^x$ trên $\mathbb{R}$ thỏa mãn $F(0)=1$. Xét tính đúng sai của các khẳng định sau
	\choiceTFt
	{$F'(0)=0$} 
	{\True $\displaystyle\int f(x) \mathrm{\,d}x = e^x + C$} 
	{\True $F(1)=e$} 
	{\True $\displaystyle\int \dfrac{f(x)}{xe^x} \mathrm{\,d}x = \ln|x| + C$} 
	\loigiai{Ta có $F(x) =\displaystyle\int f(x) \mathrm{\,d}x= \displaystyle\int e^x \mathrm{\,d}x = e^x + C$.\\
		Vì $F(0)=1$ nên $e^0 + C = 1 \Rightarrow 1 + C = 1 \Rightarrow C=0$.\\
		Vậy $F(x)=\mathrm{e}^x$.
		\begin{itemchoice}	
			\itemch Sai. Ta có  $F'(x) = f(x) = \mathrm{e}^x$. Do đó $F'(0) = e^0 = 1$. 
			\itemch Đúng. $F(x) =\displaystyle\int f(x) \mathrm{\,d}x= \displaystyle\int e^x \mathrm{\,d}x = e^x + C$.
			\itemch Đúng. Ta có $F(x) = \mathrm{e}^x$. Do đó $F(1) = \mathrm{e}^1 = \mathrm{e}$.
			\itemch Đúng. $\displaystyle\int \dfrac{f(x)}{xe^x} \mathrm{\,d}x = \int \dfrac{\mathrm{e}^x}{x\mathrm{e}^x} \mathrm{\,d}x = \int \dfrac{1}{x} \mathrm{\,d}x = \ln|x| + C$.
		\end{itemchoice}			
	}
\end{ex}

