\begin{ex}%[2D4H3-1]%[Dự án A NH 2024-2025 đợt 8]%[Vovanle]
	Cho hàm số $y=f(x)$ có đạo hàm $f'(x)$ liên tục trên $\mathbb{R}$ thỏa mãn $f'(x)=2-5\sin x$ và $f(0)=10$.
	\choiceTF
	{\True $f(\pi) = 2\pi$}
	{$\displaystyle\int f'(x)\mathrm{\,d}x=2x-5\cos x+C$, với $C$ là hằng số}
	{\True $f(x)=2x+5\cos x+5$}
	{\True Diện tích $S$ của hình phẳng $(H)$ giới hạn bởi các đường cong $y=f(x)$; $y=g(x)=5\cos x+9$ và trục tung bằng $4$}
	\loigiai{
		Ta có $f(x)=\displaystyle\int f'(x)\mathrm{\,d}x=\displaystyle\int \left(2-5\sin x\right)\mathrm{\,d}x=2x+5\cos x+C$.\\
		$f(0) = 10\Leftrightarrow 2\cdot0+5\cos0+C=10\Leftrightarrow C=5$.\\
		Suy ra  $f(x)=2x+5\cos x+5$.\\

		$f(\pi) = 2\pi$
		\begin{itemchoice}
			\itemch  Vì $f(\pi)=2\pi+5\cos \pi+5=2\pi$.
			\itemch  Vì $\displaystyle\int f'(x)\mathrm{\,d}x=\displaystyle\int \left(2-5\sin x\right)\mathrm{\,d}x=2x+5\cos x+C$.
			\itemch Ta có $f(x)=2x+5\cos x+5$.
			\itemch  Phương tình hoành độ giao điểm của $f(x)$ và $g(x)$ là
			$$2x+5\cos x+5=5\cos x+9\Leftarrow x=2.$$
			Do đó diện tích hình $(H)$ được tính bởi 
			$$S=\displaystyle\int\limits_2^4 \left|\left(2x+5\cos x+5\right)-\left(5\cos x+9\right)\right|\mathrm{\,d}x=\displaystyle\int\limits_2^4 \left|2x-4\right|\mathrm{\,d}x=\displaystyle\int\limits_2^4 \left(2x-4\right)\mathrm{\,d}x=4.$$
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D4H2-2]%[Dự án A NH 2024-2025 đợt 8]%[Vovanle]
	Cho số thực $a$ và hàm số $f(x) = \heva{&2x&\text{khi }x\le0\\&a\left(x-x^2\right)&\text{khi }x>0}$
	\choiceTF
	{\True $\displaystyle\int\limits_{-1}^0 f(x) \mathrm{\,d}x=\displaystyle\int\limits_{-1}^0 2x \mathrm{\,d}x$}
	{$\displaystyle\int\limits_{0}^1 f(x) \mathrm{\,d}x=-\dfrac{a}{6}$}
	{\True Khi $a=2$, $\displaystyle\int\limits_{-1}^1 f(x) \mathrm{\,d}x=-\dfrac{2}{3}$}
	{Điều kiện cần cà đủ để $\displaystyle\int\limits_{-1}^2 f(x) \mathrm{\,d}x>3$ là $a>-6$}
	\loigiai{
		\begin{itemchoice}
			\itemch  $\displaystyle\int\limits_{-1}^0 f(x) \mathrm{\,d}x=\displaystyle\int\limits_{-1}^0 2x\mathrm{\,d}x$.
			\itemch  $\displaystyle\int\limits_{0}^1 f(x) \mathrm{\,d}x=\displaystyle\int\limits_{0}^1 a\left(x-x^2\right)\mathrm{\,d}x=a\left(\dfrac{x^2}{2}-\dfrac{x^3}{3}\right)\Bigg|_0^1=a\left(\dfrac{1^2}{2}-\dfrac{1^3}{3}\right)=\dfrac{a}{6}$.
			\itemch  Khi $a=2$, $\displaystyle\int\limits_{-1}^1 f(x) \mathrm{\,d}x=\displaystyle\int\limits_{-1}^0 2x \mathrm{\,d}x+\displaystyle\int\limits_{0}^1 2\left(x-x^2\right) \mathrm{\,d}x=-1+\dfrac{1}{3}=-\dfrac{2}{3}$.
			\itemch  Ta có 
			\begin{eqnarray*}
				\displaystyle\int\limits_{-1}^2 f(x) \mathrm{\,d}x&=&\displaystyle\int\limits_{-1}^0 2x \mathrm{\,d}x+\displaystyle\int\limits_{0}^2 a\left(x-x^2\right) \mathrm{\,d}x\\
				&=&-1+a\left(\dfrac{x^2}{2}-\dfrac{x^3}{3}\right)\Bigg|_0^2=-1-\dfrac{2a}{3}	
			\end{eqnarray*}
			Do đó $\displaystyle\int\limits_{-1}^2 f(x) \mathrm{\,d}x>3\Leftrightarrow -1-\dfrac{2a}{3}>3\Leftrightarrow a<-6$.
		\end{itemchoice}
	}
\end{ex}

