\begin{ex}%[2D4H3-3]%[Dự án đề KT GHKII Khối 12 NH24-25-XuanVyPham]%[THPT Lê Lợi-Kon Tum]
	Cho hàm số $y=f(x)$ liên tục trên $\mathbb{R}$ và có đồ thị là đường cong trong hình dưới đây. Gọi $(H)$ là hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục hoành và các đường thẳng $x=-1$, $x=4$. 
	\begin{center}
		\begin{tikzpicture}[x=0.85cm,y=0.85cm,>=stealth,font=\footnotesize]
			\def\xmin{-1.57};
			\def\xmax{4.44};
			\def\ymin{-2.5};
			\def\ymax{2.5};
			%Tô màu
			\fill[blue!20,smooth] (-1,0) --
			plot[domain=-1:1](\x,{0.3*((\x)+1)*((\x)-1)*((\x)-4)})
			--(1,0)-- cycle;
			\fill[blue!20,smooth] (1,0) --
			plot[domain=1:4](\x,{0.3*((\x)+1)*((\x)-1)*((\x)-4)})
			--(4,0)-- cycle;
			% Tiến hành vẽ hai trục tọa độ
			\draw[->, thick] (\xmin-0.5,0) -- (\xmax+0.7,0) node[below] {$x$};
			\draw[->,thick] (0,\ymin) -- (0,\ymax) node[right]{$y$};
			\draw (0,0) node[below left] {$O$};
			%Vẽ đồ thị 
			\draw[smooth, blue,thick] plot[domain=\xmin:\xmax](\x,{0.3*((\x)+1)*((\x)-1)*((\x)-4)}) node[right]{$y=f(x)$};
			%Đánh số
			\foreach \x in 	{-1,1}
			\draw[shift={(\x,0)},color=black,thick] (0pt,2pt) -- (0pt,-2pt) node[below left]{$\x$};
			\foreach \x in 	{4}
			\draw[shift={(\x,0)},color=black,thick] (0pt,2pt) -- (0pt,-2pt) node[below right]{$\x$};
		\end{tikzpicture}
	\end{center}
	Khi đó
	\choiceTF
	{\True Diện tích hình phẳng $(H)$ là $S=\displaystyle \int \limits_{-1}^{4}\left| f(x) \right|\mathrm{\,d}x$}
	{\True Nếu $F(x)$ là một nguyên hàm của $f(x)$ thì $F(-1)>F(4)$}
	{Diện tích hình phẳng $(H)$ là $S=\displaystyle \int \limits_{-1}^{1}f(x)\mathrm{\,d}x+\displaystyle \int \limits_{1}^{4}f(x)\mathrm{\,d}x$}
	{\True Thể tích vật thể được tạo thành khi quay $(H)$ quanh trục hoành là $V=\displaystyle \int \limits_{-1}^{4}f^2(x)\mathrm{\,d}x$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Đúng.} Diện tích hình phẳng $(H)$ là $S=\displaystyle \int \limits_{-1}^{4}\left| f(x) \right|\mathrm{\,d}x$.
			\itemch	\textbf{Đúng.} Gọi $S_1$ là diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục hoành và các đường thẳng $x=-1$, $x=1$. \\
			Khi đó $S_1=\displaystyle \int \limits_{-1}^{1}\left| f(x) \right|\mathrm{\,d}x=\displaystyle \int \limits_{-1}^{1} f(x) \mathrm{\,d}x$.\\
			Gọi $S_2$ là diện tích hình phẳng giới hạn bởi đồ thị hàm số $y=f(x)$, trục hoành và các đường thẳng $x=1$, $x=4$. \\
			Khi đó $S_2=\displaystyle \int \limits_{1}^{4}\left| f(x) \right|\mathrm{\,d}x=-\displaystyle \int \limits_{1}^{4} f(x) \mathrm{\,d}x$.\\
			Vì $S_1<S_2$ nên ta có
			\begin{align*}
				\displaystyle \int \limits_{-1}^{1} f(x) \mathrm{\,d}x<-\displaystyle \int \limits_{1}^{4} f(x) \mathrm{\,d}x
				&\Rightarrow F(1)-F(-1)< -\left[F(4)-F(1)\right]
				\\ 
				&\Rightarrow F(1)-F(-1)<-F(4)+F(1) \\
				&\Rightarrow F(-1)>F(4).
			\end{align*}
			\itemch	\textbf{Sai.} Diện tích hình phẳng $(H)$ là $S=S_1+S_2=\displaystyle \int \limits_{-1}^{1}f(x)\mathrm{\,d}x-\displaystyle \int \limits_{1}^{4}f(x)\mathrm{\,d}x$.
			\itemch \textbf{Đúng.} Thể tích vật thể được tạo thành khi quay $(H)$ quanh trục hoành là $$V=\displaystyle \int \limits_{-1}^{4}f^2(x)\mathrm{\,d}x.$$
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H5H1-3]%[Dự án đề KT GHKII Khối 12 NH24-25-XuanVyPham]%[THPT Lê Lợi-Kon Tum]
	Trong không gian $Oxyz$, cho ba điểm $A(-1;1;0)$, $B(1;-1;2)$, $C(1;-2;1)$. Khi đó
	\choiceTF
	{Vectơ $\overrightarrow{n}=(1;2;3)$ là một vectơ pháp tuyến của mặt phẳng $(ABC)$}
	{Phương trình mặt phẳng $(\alpha)$ qua $A$ và vuông góc với $BC$ là $x-2y-z+3=0$}
	{\True Một vectơ pháp tuyến của mặt phẳng $(ABC)$ là $\left[\overrightarrow{AB};\overrightarrow{AC}\right]$}
	{\True Vectơ $\overrightarrow{u}=(1;1;0)$ là một vectơ pháp tuyến của mặt phẳng đi qua $O$ và chứa đường thẳng $AB$}
	\loigiai{
		\begin{itemchoice}
			\itemch \textbf{Sai.} Ta có $\overrightarrow{AB}=(2;-2;2)$, $\overrightarrow{AC}=(2;-3;1)$.\\
			Vậy một vectơ pháp tuyến của mặt phẳng $(ABC)$ là $\overrightarrow{m}=\left[\overrightarrow{AB};\overrightarrow{AC}\right]=(4;2;-2)$. \\
			Ta có $\dfrac{1}{4} \neq \dfrac{2}{2} \neq \dfrac{3}{-2}$ nên  $\overrightarrow{n}$ và $\overrightarrow{m}$ không cùng phương.\\
			Do đó vectơ $\overrightarrow{n}=(1;2;3)$ không là một vectơ pháp tuyến của mặt phẳng $(ABC)$.
			\itemch	\textbf{Sai.} Phương trình mặt phẳng $(\alpha)$ đi qua $A(-1;1;0)$ và vuông góc với $BC$ nên nhận $\overrightarrow{BC}=(0;-1;-1)$ làm vectơ pháp tuyến.\\
			Khi đó $(\alpha) \colon -1(y-1)-1(z-0) =0 \Leftrightarrow  -y-z+1=0 \Leftrightarrow y+z-1=0$.
			\itemch	\textbf{Đúng.} Một vectơ pháp tuyến của mặt phẳng $(ABC)$ là $\left[\overrightarrow{AB};\overrightarrow{AC}\right]$.
			\itemch \textbf{Đúng.} Mặt phẳng đi qua $O$ và chứa đường thẳng $AB$ có một cặp vectơ chỉ phương là $\overrightarrow{AB}=(2;-2;2)$ và $\overrightarrow{OA}=(-1;1;0)$.\\
			Khi đó một vectơ pháp tuyến của mặt phẳng này là $$\left[\overrightarrow{AB};\overrightarrow{OA}\right]=\left(-2;-2;0\right)=-2\left(1;1;0\right)=-2\overrightarrow{u}.$$
			Vậy $\overrightarrow{u}$ là một vectơ pháp tuyến của mặt phẳng đi qua $O$ và chứa đường thẳng $AB$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D4H1-2]%[Dự án đề kiểm tra Toán 12 GHKIi NH24-25-Nguyễn Hữu Duy]%[THPT Lê Lợi - Kon Tum]
	Cho hàm số $F(x)$ là một nguyên hàm của hàm số $f(x)=3x^2-4x+1$ và $F(2)=2$. Tính $F(3)$.
	\par
	\shortans[oly]{12}
	\loigiai{
	Ta có $\displaystyle\int\limits_2^3 f(x)\mathrm{\,d}x=\displaystyle\int\limits_2^3 \left(3x^2-4x+1\right)\mathrm{\,d}x=\left(x^3-2x^2+x\right)\bigg|_2^3=12-2=10$.\\
	Mà $\displaystyle\int\limits_2^3 f(x)\mathrm{\,d}x=F(x)\bigg|_2^3=F(3)-F(2)$.\\
	Suy ra $F(3)=F(2)+\displaystyle\int\limits_2^3 f(x)\mathrm{\,d}x=2+10=12$.
	}
\end{ex}

\begin{ex}%[2D4H1-6]%[Dự án đề kiểm tra Toán 12 GHKIi NH24-25-Nguyễn Hữu Duy]%[THPT Lê Lợi - Kon Tum]
	Một ô tô chuyển động trên đường thẳng nằm ngang với gia tốc phụ thuộc thời gian $t$\,($s$) là $a(t)=2t-7$\,(m/s$^2$). Biết vận tốc đầu bằng $10$\,(m/s), hỏi sau bao lâu thì ô tô đạt vận tốc $18$\,(m/s)?
	\par
	\shortans[oly]{8}
	\loigiai{
	Vận tốc của ô tô được biểu diễn theo công thức \[v(t)=\displaystyle\int a(t)\mathrm{\,d}t=\displaystyle\int (2t-7)\mathrm{\,d}t=t^2-7t+C.\]
	Mà vận tốc đầu của ô tô bằng $10$\,(m/s) nên $v(0)=10\Leftrightarrow 0^2-7\cdot 0+C=10\Leftrightarrow C=10$.\\
	Suy ra $v(t)=t^2-7t+10$.\\
	Để ô tô đạt vận tốc $18$\,(m/s) thì $t^2-7t+10=18\Leftrightarrow t^2-7t-8=0\Leftrightarrow \hoac{&t=-1\, (\textbf{loại}) \\&t=8\, (\text{chọn}).}$
	}
\end{ex}

\begin{ex}%[2D4H2-2]%[Dự án đề kiểm tra Toán 12 GHKIi NH24-25-Nguyễn Hữu Duy]%[THPT Lê Lợi - Kon Tum]
	Giá trị của tích phân $\displaystyle\int\limits_0^2 |2x-3|\mathrm{\,d}x$ bằng bao nhiêu?.
	\par
	\shortans[oly]{2{,}5}
	\loigiai{
	Ta có 
	\allowdisplaybreaks
	\begin{eqnarray*}
		\displaystyle\int\limits_0^2 |2x-3|\mathrm{\,d}x &=& \displaystyle\int\limits_0^{\tfrac{3}{2}} |2x-3|\mathrm{\,d}x + \displaystyle\int\limits_{\tfrac{3}{2}}^2 |2x-3|\mathrm{\,d}x \\
		&=& -\displaystyle\int\limits_0^{\tfrac{3}{2}} (2x-3)\mathrm{\,d}x + \displaystyle\int\limits_{\tfrac{3}{2}}^2 (2x-3)\mathrm{\,d}x \\
		&=& -\left(x^2-3x\right)\bigg|_0^{\tfrac{3}{2}}+\left(x^2-3x\right)\bigg|_{\tfrac{3}{2}}^2\\
		&=& \dfrac{9}{4}+\dfrac{1}{4}= \dfrac{5}{2}=2{,}5.
	\end{eqnarray*}
	}
\end{ex}

\begin{ex}%[2D4H3-1]%[Dự án đề kiểm tra Toán 12 GHKIi NH24-25-Nguyễn Hữu Duy]%[THPT Lê Lợi - Kon Tum]
	Cho hàm số $y=f(x)$. Đồ thị hàm số $y=f'(x)$ là đường cong trong hình dưới. Biết rằng diện tích của các phần hình phẳng $A$ và $B$ lần lượt là $S_A=4$ và $S_B=10$. Biết giá trị của $f(0)=2$, giá trị của $f(3)$ bằng bao nhiêu?
	\begin{center}			
		\begin{tikzpicture}[scale=1,>=stealth, font=\footnotesize, line join=round, line cap=round]
			\begin{scope}
				\clip (-2,-3) rectangle (4,3);
				\fill[cyan!30,smooth]
				(0,0)--plot[domain=0:1] (\x,{(\x)*(\x-1)*(\x-3)})
				-- (1,0)
				-- cycle;
				\fill[orange!30,smooth]
				(1,0)--plot[domain=1:3] (\x,{(\x)*(\x-1)*(\x-3)})
				-- (3,0)
				-- cycle;
				\draw[domain=-3:4, samples=100] 
				plot (\x, {(\x)*(\x-1)*(\x-3)});
			\end{scope}
			\draw[->] (-2,0) -- (4,0) node[below] {$x$};
			\draw[->] (0,-3) -- (0,3) node[left] {$y$};
			\fill 
			(0,0) circle (1pt) node[below left]{$O$}
			(1,0) circle (1pt) node[below]{$1$}
			(3,0) circle (1pt) node[below,xshift=1mm]{$3$};
			\node at (2.5,3) {$y=f'(x)$};
			\node at (0.5,.25) {$A$};
			\node at (2.2,-1) {$B$};
		\end{tikzpicture}
	\end{center}
	\par
	\shortans[oly]{8}
	\loigiai{
	Từ đồ thị ta có
	\begin{itemize}
		\item $S_A=4\Leftrightarrow \displaystyle\int\limits_0^1 f'(x)\mathrm{\,d}x=4\Leftrightarrow f(x)\bigg|_0^1=4\Leftrightarrow f(1)-f(0)=4$.\tagEX{1}
		\item $S_B=10\Leftrightarrow -\displaystyle\int\limits_1^3 f'(x)\mathrm{\,d}x=4\Leftrightarrow -f(x)\bigg|_1^3=10\Leftrightarrow f(1)-f(3)=10$.\tagEX{2}
	\end{itemize}
	Lấy vế của $(2)$ trừ vế của $(1)$ta có $f(3)-f(0)=6\Leftrightarrow f(3)=f(0)+6=2+6=8$.
	}
\end{ex}

