	}
\end{ex}

\begin{ex}%[2D4V2-2]%[2-TN-DS-TLN-thpt-ngoc-lac-thanh-hoa-GHKII-NH24-25]%[Hector Tran]
	Cho hàm số $f(x)$ liên tục trên $\mathbb{R}$ thỏa mãn điều kiện $f(0)=2\sqrt{2}$, $f(x) > 0$, $\forall x\in\mathbb{R}$ và $f(x)\cdot f'(x)=(2x+1)\sqrt{1+f^2(x)}$, $\forall x\in\mathbb{R}$. Tính giá trị $f(1)$. Kết quả làm tròn đến một chữ số đằng sau dấu phẩy.
	\shortans[oly]{4,9}
	\loigiai{
		Ta có \allowdisplaybreaks
		\begin{eqnarray*}
			&&f(x)\cdot f'(x)=(2x+1)\sqrt{1+f^2(x)},\ \forall x\in\mathbb{R}\\
			&\Leftrightarrow&\dfrac{f(x).f'(x)}{\sqrt{1+f^2(x)}}=2x+1\\			
			&\Leftrightarrow&\displaystyle\int\dfrac{f(x)\cdot f'(x)}{\sqrt{1+f^2(x)}}\mathrm{\,d}x=\displaystyle\int(2x+1)\mathrm{\,d}x\\
			&\Leftrightarrow&\sqrt{1+f^2(x)}=x^2+x+C.
		\end{eqnarray*}		
		Vì $f(0)=2\sqrt{2}$ nên $C=3$.\\
		Suy ra $f(x)=\sqrt{(x^2+x+3)^2-1}\Rightarrow f(1)=2\sqrt{6}\approx 4{,}9$.
	}
\end{ex}

