\begin{ex}%[2D1H3-1]%[2-TN-DS-TLN-thpt-nguyen-du-nam-dinh-GHKII-NH24-25]%[Lê Phúc]
	Cho hàm số $y=\dfrac{x+3}{x-2}$
	. Xét tính đúng sai của mỗi mệnh đề sau
	\choiceTF
	{\True Hàm số đồng biến trên mỗi khoảng $(-\infty;2)$, $(2;+\infty)$}
	{\True Đồ thị hàm số có đường tiệm cận đứng là $x=2$}
	{\True Đồ thị hàm số cắt trục tung tại điểm $A\left(0;-\dfrac{3}{2}\right)$}
	{\True Giá trị lớn nhất của hàm số trên đoạn $[-2;1]$ bằng $-4$}
	\loigiai{
		Tập xác định $\mathscr{D}=\mathbb{R}\setminus \{2\}$.\\
		Ta có $y'=\dfrac{-5}{(x-2)^2}<0$, $\forall x\in \mathscr{D}$.
		\begin{itemchoice}
			\itemch Do $y'<0$, $\forall x\in  \mathscr{D}$ nên hàm số đồng biến trên mỗi khoảng $(-\infty;2)$, $(2;+\infty)$.
			\itemch Ta có $\lim\limits_{x\to 2^+}f(x)=\lim\limits_{x\to 2^+}\dfrac{x+3}{x-2}=+\infty$ vì $\heva{&\lim\limits_{x\to 2^+}(x+3)=5>0\\&\lim\limits_{x\to 2^+}(x-2)=0\\&x-2>0 \ (\text{vì} \ x\to 2^+ \Rightarrow x>2).}$\\
			Suy ra $x=2$ là tiệm cận đứng của đồ thị hàm số.
			\itemch Thay $x=0$ vào hàm số $y=\dfrac{x+3}{x-2}$ ta có
			\[y=\dfrac{0+3}{0-2}=-\dfrac{3}{2}.\]
			Suy ra đồ thị hàm số cắt trục tung tại điểm $A\left(0;-\dfrac{3}{2}\right)$.
			\itemch Do hàm số đồng biển trên $[-2;1]$ nên giá trị lớn nhất của hàm số trên đoạn $[-2;1]$ là
			\[y(1)=\dfrac{1+3}{1-2}=-4.\]
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2H5H1-3]%[2-TN-DS-TLN-thpt-nguyen-du-nam-dinh-GHKII-NH24-25]%[Lê Phúc]
	Trong không gian $Oxyz$, cho điểm $M(1;1;-2)$ và mặt phẳng \\ $(P)\colon
	2x-y+2z-3=0$.
	\choiceTF
	{\True Mặt phẳng $(P)$ vuông góc với mặt phẳng $(Q)\colon x+2y-3=0$}
	{\True Khoảng cách từ điểm $M$ đến mặt phẳng $(P)$ bằng $2$}
	{Mặt phẳng $(P)$ có vectơ pháp tuyến là $\overrightarrow{n}=(2;1;2)$}
	{Mặt phẳng đi qua điểm $M$ và song song với mặt phẳng $(P)$ có phương trình là \\ $2x+y+2z+1=0$}
	\loigiai{
		\begin{itemchoice}
			\itemch Ta có $\overrightarrow{n}_{(P)}\cdot\overrightarrow{n}_{(Q)}=2\cdot1+(-1)\cdot 2+(-3)\cdot 0=0$.\\
			Suy ra $(P)$ vuông góc với $(Q)$.
			\itemch $\mathrm{\,d}(M,(P))=\dfrac{|2\cdot 1-1+2(-2)-3|}{\sqrt{2^2+(-1)^2+2^2}}=2$.
			\itemch Mặt phẳng $(P)$ có vectơ pháp tuyến là $\overrightarrow{n}=(2;-1;2)$.
			\itemch 
			Mặt phẳng song song với $(P)$ có dạng là $2x-y+2z+d=0\ (d\neq1)$.\\
		Mặt khác mặt phẳng đi qua điểm $M$ nên
		\[2\cdot1-1+2\cdot (-2)+d=0\Rightarrow d=3 \ \text{(thỏa mãn)}.\]
		Vậy mặt phẳng đi qua điểm $M$ và song song với mặt phẳng $(P)$ có phương trình là \\ $2x-y+2z+3=0$.
		\end{itemchoice}
	}
\end{ex}

\begin{ex}%[2D3H1-3]%[2-TN-DS-TLN-thpt-nguyen-du-nam-dinh-GHKII-NH24-25]%[Lê Phúc]
	Khảo sát thời gian xem điện thoại trong một ngày của một số học sinh khối $12$ thu được mẫu số liệu ghép nhóm sau
		\begin{center}
		\begin{tabular}{|c|c|c|c|c|c|}
			\hline 
			Thời gian (phút) & $[0;20)$ & $[20;40)$ & $[40;60)$ & $[60;80)$ & $[80;100)$\\
			\hline
			Số học sinh & $4$ & $8$ & $12$ & $10$ & $8$\\
			\hline
		\end{tabular}
	\end{center}
	\choiceTF
	{\True  Phương sai của mẫu số liệu bằng $605$ (kết quả làm tròn đến hàng đơn vị)}
	{Số trung bình của mẫu số liệu trên là $54{,}7$ (kết quả làm tròn đến hàng phần mười)}
	{\True Khoảng tứ phân vị của mẫu số liệu bằng $38{,}75$}
	{Tứ phân vị thứ hai của mẫu số liệu bằng $54$}
	\loigiai{
			\begin{center}
			\begin{tabular}{|c|c|c|c|c|c|}
				\hline 
				Thời gian (phút) & $[0;20)$ & $[20;40)$ & $[40;60)$ & $[60;80)$ & $[80;100)$\\
				\hline
				Giá trị đại diện & $10$ & $30$ & $50$ & $70$ & $90$\\
				\hline
				Số học sinh & $4$ & $8$ & $12$ & $10$ & $8$\\
				\hline
			\end{tabular}
		\end{center}
		Số trung bình là
		\[\overline{x}=\dfrac{10\cdot 4+30\cdot 8+50\cdot 12+70\cdot 10+90\cdot 8}{42}=\dfrac{1\,150}{21}.\]
		\begin{itemchoice}
		\itemch Ta có 
		\allowdisplaybreaks
		\begin{eqnarray*}
			s^2&=&\dfrac{1}{42}\cdot \left[4\cdot10^2+8\cdot30^2+12\cdot50^2+10\cdot70^2+8\cdot90^2\right]-\left(\dfrac{1\,150}{21}\right)^2\\
			&\approx&605.
		\end{eqnarray*}
		\itemch 
		Số trung bình là $\overline{x}=\dfrac{1\,150}{21}\approx54{,}8$.
		\itemch 
		\begin{itemize}
			\item Vì $Q_1\in [20;40]$ nên $Q_1=20+\dfrac{\dfrac{42}{4}-4}{8}\cdot (40-20)=\dfrac{145}{4}$.\\
			\item Vì $Q_3\in [60;80]$ nên $Q_3=60+\dfrac{\dfrac{42\cdot 3}{4}-(4+8+12)}{10}\cdot (80-60)=75$.
		\end{itemize}
		Khoảng tứ phân vị $\Delta Q=Q_3-Q_1=75-\dfrac{145}{4}=38{,}3=75$.
		\itemch Vì $Q_2\in [40;60]$ nên $Q_2=40+\dfrac{\dfrac{42}{2}-(4+8)}{12}\cdot (60-40)=55$.
	\end{itemchoice} 
	}
\end{ex}

\begin{ex}%[2D4H1-4]%[2-TN-DS-TLN-thpt-nguyen-du-nam-dinh-GHKII-NH24-25]%[Lê Phúc]
	Cho $F(x)$ là một nguyên hàm của hàm số $f(x)=3x^2+\dfrac{1}{x}+2$.
	\choiceTF
	{Nếu $F(1)=2$ thì $F(x)=x^3-\ln|x|+2x-1$}
	{$F'(x)=6x-\dfrac{1}{x^2}$}
	{\True $\displaystyle\int\limits_{1}^e f(x)\mathrm{\,d}x \approx 23{,}5$}
	{$\displaystyle\int\limits \left(3x^2+\dfrac{1}{x}+2\right)\mathrm{\,d}x=x^3+\ln|x|+C$}
	\loigiai{
			\begin{itemchoice}
			\itemch 
			$F(x)=\displaystyle\int\limits f(x)\mathrm{\,d}x=\displaystyle\int\limits \left(3x^2+\dfrac{1}{x}+2\right)\mathrm{\,d}x=x^3+\ln|x|+2x+C$.\\
			Ta lại có $F(1)=2\Leftrightarrow C=-1$.\\
			Suy ra $F(x)=x^3+\ln|x|+2x-1$.
			\itemch $F'(x)=f(x)=3x^2+\dfrac{1}{x}+2$.
			\itemch $\displaystyle\int\limits_{1}^e f(x)\mathrm{\,d}x = \left(x^3+\ln|x|+2x-1\right)\left|_1^e \right.=e^3+2e-2\approx23{,}5$.
			\itemch $\displaystyle\int\limits \left(3x^2+\dfrac{1}{x}+2\right)\mathrm{\,d}x=x^3+\ln|x|+2x+C$.
		\end{itemchoice} 
	}
\end{ex}

\begin{ex}%[2D1V2-1]%[Lớp 12 - Ôn tập giữa học kì 2 - THPT Nguyễn Du - Nam Định]%[Phạm Hoàng Việt]
