		\draw[fill=blue!30!white] plot[domain=0:360, samples=100] ({2*cos(\x)},{-0.2+0.8*sin(\x)});
		\draw[fill=blue!30!white] plot[domain=0:360, samples=100] ({2*cos(\x)},{0.8*sin(\x)});
		\foreach\x/\y in {A/180,B/45,C/0,O/90}{
			\draw[fill=black] (\x) circle (1pt) ($(\x)+(\y:3mm)$) node {$\x$};
		}
		\draw (O)--(A) (O)--(B) (O)--(C);
		\draw[thick, blue, ->] (O)--($(O)!0.5!(A)$) node[midway,left] {$\overrightarrow{F}_1$};
		\draw[thick, blue, ->] (O)--($(O)!0.5!(C)$) node[midway,right] {$\overrightarrow{F}_3$};
		\draw[thick, blue, ->] (O)--($(O)!0.7!(B)$) node[midway,right] {$\overrightarrow{F}_2$};
		\draw[dashed] (C)--(I)--($(I)+(0,-2)$);
		\draw[thick, ->] ({2*cos(270)},{-0.2+0.8*sin(270)})--($(I)+(0,-2)$) node[midway, right] {$\overrightarrow{F}$};
