		\fill (0,0) circle(1pt) node[above right]{$O$};
		\draw[name path=f, samples=150,smooth,domain=-.5:3.1] plot(\x,{(\x)^2-2*(\x)});
		\draw (3,-1)--(3,3.2);
		\path[fill,fill opacity=0.1, domain=0:2] plot(\x,{(\x)^2-2*(\x)})--(0,0)--(2,0)--cycle;
