		(2,0)-- plot(\x,{(\x)^2-2*(\x)})--(3,0)--cycle;
		\draw (1.2,-0.5)node[left]{$A$}
		(2.7,0.5)node[left]{$B$}    
		(3,0)node[above right]{$a$}
		(2,0)node[below]{$2$}
		;
		\end{tikzpicture}
	}
	\loigiai{
