\begin{ex}%[2D4H3-4]%[Dự án đề kiểm tra Toán 12 GKII Dot8 NH24-25-Bùi Lương Phúc]%[THPT Thanh Miện - Hải Dương]
	Biết $\displaystyle \int\limits_1^3 \dfrac{x+2}{x}\mathrm{\,d}x = a + b\ln c$, với $a$, $b$, $c \in \mathbb{Z}$, $c < 9$. Tổng $S = a + b + c$ bằng
	\choice
	{$S = 5$}
	{$S = 6$}
	{\True $S = 7$}
	{$S = 8$}
	\loigiai{
		\begin{align*}
			\displaystyle \int\limits_1^3 \dfrac{x+2}{x}\mathrm{\,d}x &= \displaystyle \int\limits_1^3 \left(1 + \dfrac{2}{x}\right)\mathrm{\,d}x = \displaystyle \int\limits_1^3 1\mathrm{\,d}x + \displaystyle \int\limits_1^3 \dfrac{2}{x}\mathrm{\,d}x \\
			&= x\Big|_1^3 + 2\ln|x|\Big|_1^3 = (3 - 1) + 2(\ln 3 - \ln 1) = 2 + 2\ln 3.
		\end{align*}
		Suy ra $a = 2$, $b = 2$, $c = 3 \Rightarrow S = 2 + 2 + 3 = 7$.
	}
\end{ex}

