		\begin{tikzpicture}[line join=round, line cap=round,>=stealth,thick]
			\tikzset{every node/.style={scale=0.9}}
			\draw[->] (-1.1,0)--(6.1,0) node[below left] {$x$};
			\draw[->] (0,-1.1)--(0,4.1) node[below left] {$y$};
			\draw (0,0) node [below left] {$O$};
